import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { wasteData } from '../../data/wasteData';
import { fadeInUp, staggerContainer } from '../../utils/animations';
import './LocalRules.css';

const LocalRules = ({ onBack, selectedLocale, setSelectedLocale }) => {
  const [activeBin, setActiveBin] = useState(null);
  const locales = Object.keys(wasteData.localeRules);
  const currentRules = wasteData.localeRules[selectedLocale];

  return (
    <div className="local-rules-page">
      <div className="container">
        <motion.button 
          className="back-btn"
          onClick={onBack}
          initial={{ opacity: 0, x: -20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.5 }}
        >
          ‚Üê Back to Home
        </motion.button>

        <motion.div
          className="page-header"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <h1>Local Recycling Rules</h1>
          <p>Understand your city's specific waste management system and sorting guidelines</p>
        </motion.div>

        {/* Location Selector */}
        <motion.div 
          className="location-selector"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.2 }}
        >
          <label htmlFor="locale-select">Select Your Location:</label>
          <select 
            id="locale-select"
            value={selectedLocale}
            onChange={(e) => setSelectedLocale(e.target.value)}
          >
            {locales.map(locale => (
              <option key={locale} value={locale}>{locale}</option>
            ))}
          </select>
        </motion.div>

        {/* Rules Overview */}
        <motion.section 
          className="rules-overview"
          variants={staggerContainer}
          initial="initial"
          animate="animate"
        >
          <motion.h2 variants={fadeInUp}>
            Recycling System in {selectedLocale}
          </motion.h2>
          
          <motion.div 
            className="bins-grid"
            variants={fadeInUp}
          >
            {Object.entries(currentRules.colors).map(([color, info]) => (
              <motion.div
                key={color}
                className={`bin-card ${color.toLowerCase()} ${activeBin === color ? 'active' : ''}`}
                variants={fadeInUp}
                whileHover={{ scale: 1.05 }}
                onClick={() => setActiveBin(activeBin === color ? null : color)}
              >
                <div className="bin-color-indicator"></div>
                <div className="bin-info">
                  <h3>{color} Bin</h3>
                  <p className="bin-category">{info.category}</p>
                  <p className="bin-tip">{info.tips}</p>
                  
                  {activeBin === color && (
                    <motion.div 
                      className="bin-details"
                      initial={{ opacity: 0, height: 0 }}
                      animate={{ opacity: 1, height: 'auto' }}
                      transition={{ duration: 0.3 }}
                    >
                      <h4>Common Items:</h4>
                      <ul>
                        {info.items.map(item => (
                          <li key={item}>‚úì {item}</li>
                        ))}
                      </ul>
                      
                      {currentRules.pickup && currentRules.pickup[color] && (
                        <div className="pickup-schedule">
                          <h4>üóìÔ∏è Pickup Schedule:</h4>
                          <p>{currentRules.pickup[color]}</p>
                        </div>
                      )}
                    </motion.div>
                  )}
                </div>
              </motion.div>
            ))}
          </motion.div>
        </motion.section>

        {/* Quick Guide */}
        <motion.section 
          className="quick-guide"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.4 }}
        >
          <h2>Quick Recycling Guide</h2>
          <div className="guide-tips">
            <div className="tip-card">
              <div className="tip-icon">üßπ</div>
              <h4>Clean & Dry</h4>
              <p>Always rinse containers and let them dry before recycling</p>
            </div>
            <div className="tip-card">
              <div className="tip-icon">üö´</div>
              <h4>No Plastic Bags</h4>
              <p>Never put recyclables in plastic bags - they jam machinery</p>
            </div>
            <div className="tip-card">
              <div className="tip-icon">üè∑Ô∏è</div>
              <h4>Check Labels</h4>
              <p>Look for recycling symbols and follow local guidelines</p>
            </div>
            <div className="tip-card">
              <div className="tip-icon">üìû</div>
              <h4>When in Doubt</h4>
              <p>Contact your local waste management authority</p>
            </div>
          </div>
        </motion.section>

        {/* Common Mistakes */}
        <motion.section 
          className="common-mistakes"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.6 }}
        >
          <h2>Common Recycling Mistakes</h2>
          <div className="mistakes-list">
            <div className="mistake-item">
              <div className="mistake-icon">‚ùå</div>
              <div className="mistake-content">
                <h4>Pizza Boxes with Grease</h4>
                <p>Greasy cardboard cannot be recycled. Cut out clean parts only.</p>
              </div>
            </div>
            <div className="mistake-item">
              <div className="mistake-icon">‚ùå</div>
              <div className="mistake-content">
                <h4>Dirty Containers</h4>
                <p>Food residue contaminates entire recycling batches.</p>
              </div>
            </div>
            <div className="mistake-item">
              <div className="mistake-icon">‚ùå</div>
              <div className="mistake-content">
                <h4>Mixed Materials</h4>
                <p>Items with multiple materials often cannot be recycled together.</p>
              </div>
            </div>
            <div className="mistake-item">
              <div className="mistake-icon">‚ùå</div>
              <div className="mistake-content">
                <h4>Small Items</h4>
                <p>Items smaller than a credit card fall through sorting machinery.</p>
              </div>
            </div>
          </div>
        </motion.section>

        {/* Emergency Contacts */}
        <motion.section 
          className="emergency-contacts"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.8 }}
        >
          <h2>Need Help?</h2>
          <div className="contacts-grid">
            <div className="contact-card">
              <div className="contact-icon">üìû</div>
              <h4>Waste Management Hotline</h4>
              <p>+1 (800) 555-RECYCLE</p>
              <button className="btn btn-small">Call Now</button>
            </div>
            <div className="contact-card">
              <div className="contact-icon">üåê</div>
              <h4>Local Website</h4>
              <p>Visit your city's waste management website</p>
              <button className="btn btn-small">Visit Site</button>
            </div>
            <div className="contact-card">
              <div className="contact-icon">üì±</div>
              <h4>Mobile App</h4>
              <p>Download your city's recycling app</p>
              <button className="btn btn-small">Download</button>
            </div>
          </div>
        </motion.section>
      </div>
    </div>
  );
};

export default LocalRules;
