import React, { useEffect, useRef } from 'react';
import { motion } from 'framer-motion';
import { fadeInUp, staggerContainer } from '../../utils/animations';
import './Home.css';

const Home = ({ onNavigate, selectedLocale, setSelectedLocale }) => {
  const featuresRef = useRef([]);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            entry.target.classList.add('visible');
          }
        });
      },
      { threshold: 0.1 }
    );

    featuresRef.current.forEach(el => {
      if (el) observer.observe(el);
    });

    return () => observer.disconnect();
  }, []);

  const features = [
    {
      icon: 'üì∑',
      title: 'AI-Powered Identification',
      description: 'Simply take a photo or upload an image to identify waste items instantly.'
    },
    {
      icon: 'üóëÔ∏è',
      title: 'Smart Sorting',
      description: 'Get accurate disposal instructions based on your local recycling rules.'
    },
    {
      icon: '‚ôªÔ∏è',
      title: 'Upcycling Ideas',
      description: 'Discover creative ways to reuse items instead of throwing them away.'
    },
    {
      icon: 'üåç',
      title: 'Localized Guidance',
      description: 'Tailored advice for your city\'s specific waste management system.'
    }
  ];

  const stats = [
    { number: '90%', label: 'Recycling Accuracy' },
    { number: '50+', label: 'Items Identifiable' },
    { number: '15+', label: 'Cities Supported' },
    { number: '24/7', label: 'Available' }
  ];

  return (
    <div className="home-page">
      {/* Hero Section */}
      <section className="hero-section">
        <div className="container">
          <div className="hero-content">
            <motion.div 
              className="hero-text"
              variants={staggerContainer}
              initial="initial"
              animate="animate"
            >
              <motion.h1 variants={fadeInUp}>
                Smart Recycling Made <span className="highlight">Simple</span>
              </motion.h1>
              <motion.p variants={fadeInUp} className="hero-description">
                Identify, sort, and recycle correctly with AI-powered waste classification. 
                Reduce contamination and discover creative upcycling ideas for a sustainable future.
              </motion.p>
              
              <motion.div variants={fadeInUp} className="hero-actions">
                <motion.button 
                  className="btn btn-primary btn-large"
                  onClick={() => onNavigate('identification')}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  üîç Identify Your Item
                </motion.button>
                <motion.button 
                  className="btn btn-secondary btn-large"
                  onClick={() => onNavigate('upcycling')}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  ‚ôªÔ∏è Upcycling Ideas
                </motion.button>
              </motion.div>

              <motion.div variants={fadeInUp} className="locale-selector">
                <label>üìç Your Location:</label>
                <select 
                  value={selectedLocale}
                  onChange={(e) => setSelectedLocale(e.target.value)}
                >
                  <option value="India">India</option>
                  <option value="Germany">Germany</option>
                  <option value="USA">United States</option>
                  <option value="UK">United Kingdom</option>
                </select>
              </motion.div>
            </motion.div>

            <motion.div 
              className="hero-visual"
              initial={{ opacity: 0, x: 50 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8, delay: 0.2 }}
            >
              <div className="recycling-animation">
                {/* Animated Recycling Process */}
                <div className="recycling-process">
                  {/* Waste Items */}
                  <motion.div 
                    className="waste-item plastic"
                    animate={{ 
                      y: [0, -20, 0],
                      rotate: [0, 5, -5, 0]
                    }}
                    transition={{ 
                      duration: 4,
                      repeat: Infinity,
                      ease: "easeInOut"
                    }}
                  >
                    üß¥
                  </motion.div>
                  
                  <motion.div 
                    className="waste-item paper"
                    animate={{ 
                      y: [0, -15, 0],
                      rotate: [0, -3, 3, 0]
                    }}
                    transition={{ 
                      duration: 3.5,
                      repeat: Infinity,
                      ease: "easeInOut",
                      delay: 0.5
                    }}
                  >
                    üìÑ
                  </motion.div>
                  
                  <motion.div 
                    className="waste-item glass"
                    animate={{ 
                      y: [0, -25, 0],
                      rotate: [0, 4, -4, 0]
                    }}
                    transition={{ 
                      duration: 4.2,
                      repeat: Infinity,
                      ease: "easeInOut",
                      delay: 1
                    }}
                  >
                    üç∂
                  </motion.div>

                  {/* Recycling Arrows */}
                  <motion.div 
                    className="recycling-arrow"
                    animate={{ 
                      scale: [1, 1.1, 1],
                      opacity: [0.7, 1, 0.7]
                    }}
                    transition={{ 
                      duration: 2,
                      repeat: Infinity,
                      ease: "easeInOut"
                    }}
                  >
                    ‚Üª
                  </motion.div>

                  {/* Recycling Bin */}
                  <motion.div 
                    className="recycling-bin"
                    animate={{ 
                      scale: [1, 1.05, 1]
                    }}
                    transition={{ 
                      duration: 3,
                      repeat: Infinity,
                      ease: "easeInOut"
                    }}
                  >
                    üóëÔ∏è
                  </motion.div>

                  {/* Recycled Products */}
                  <motion.div 
                    className="recycled-item plant"
                    animate={{ 
                      y: [0, -10, 0],
                      scale: [1, 1.1, 1]
                    }}
                    transition={{ 
                      duration: 3.5,
                      repeat: Infinity,
                      ease: "easeInOut",
                      delay: 2
                    }}
                  >
                    üå±
                  </motion.div>
                  
                  <motion.div 
                    className="recycled-item new-product"
                    animate={{ 
                      y: [0, -15, 0],
                      rotate: [0, 8, -8, 0]
                    }}
                    transition={{ 
                      duration: 4,
                      repeat: Infinity,
                      ease: "easeInOut",
                      delay: 1.5
                    }}
                  >
                    üéÅ
                  </motion.div>
                </div>

                {/* Connection Lines */}
                <div className="connection-line line-1"></div>
                <div className="connection-line line-2"></div>
                <div className="connection-line line-3"></div>
                <div className="connection-line line-4"></div>
              </div>
            </motion.div>
          </div>
        </div>
      </section>

      {/* Stats Section */}
      <section className="stats-section">
        <div className="container">
          <div className="stats-grid">
            {stats.map((stat, index) => (
              <motion.div
                key={stat.label}
                className="stat-card"
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
                viewport={{ once: true }}
              >
                <div className="stat-number">{stat.number}</div>
                <div className="stat-label">{stat.label}</div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section className="features-section">
        <div className="container">
          <motion.div 
            className="section-header"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            <h2>How It Works</h2>
            <p>Three simple steps to proper waste disposal</p>
          </motion.div>

          <div className="features-grid">
            {features.map((feature, index) => (
              <motion.div
                key={feature.title}
                ref={el => featuresRef.current[index] = el}
                className="feature-card fade-in"
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                viewport={{ once: true }}
              >
                <div className="feature-icon">{feature.icon}</div>
                <h3>{feature.title}</h3>
                <p>{feature.description}</p>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="cta-section">
        <div className="container">
          <motion.div 
            className="cta-content"
            initial={{ opacity: 0, scale: 0.9 }}
            whileInView={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            <h2>Ready to Recycle Smarter?</h2>
            <p>Join thousands of users making sustainable choices every day</p>
            <motion.button 
              className="btn btn-primary btn-large"
              onClick={() => onNavigate('identification')}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              Start Identifying Now
            </motion.button>
          </motion.div>
        </div>
      </section>
    </div>
  );
};

export default Home;
