import React, { useState } from 'react';
import { motion } from 'framer-motion';
import './Header.css';

const Header = ({ currentPage, onNavigate, darkMode, toggleDarkMode }) => {
  const [showScheduleForm, setShowScheduleForm] = useState(false);

  const navItems = [
    { id: 'home', label: 'Home', icon: 'üè†' },
    { id: 'identification', label: 'Identify', icon: 'üîç' },
    { id: 'local-rules', label: 'Local Rules', icon: 'üìã' },
    { id: 'upcycling', label: 'Upcycling', icon: '‚ôªÔ∏è' }
  ];

  return (
    <>
      <header className="header">
        <div className="container">
          <div className="header-content">
            <motion.div 
              className="logo"
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.5 }}
            >
              <span className="logo-icon">‚ôªÔ∏è</span>
              <h1>Smart Recycle Identifier</h1>
            </motion.div>

            <nav className="nav">
              {navItems.map((item) => (
                <motion.button
                  key={item.id}
                  className={`nav-btn ${currentPage === item.id ? 'active' : ''}`}
                  onClick={() => onNavigate(item.id)}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  <span className="nav-icon">{item.icon}</span>
                  {item.label}
                </motion.button>
              ))}
              
              {/* Schedule Pickup Button */}
              <motion.button
                className="nav-btn schedule-btn"
                onClick={() => setShowScheduleForm(true)}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <span className="nav-icon">üìÖ</span>
                Schedule Pickup
              </motion.button>
            </nav>

            <motion.button 
              className="theme-toggle"
              onClick={toggleDarkMode}
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
            >
              {darkMode ? '‚òÄÔ∏è' : 'üåô'}
            </motion.button>
          </div>
        </div>
      </header>

      {/* Schedule Pickup Form Modal */}
      {showScheduleForm && (
        <SchedulePickupForm 
          onClose={() => setShowScheduleForm(false)}
          darkMode={darkMode}
        />
      )}
    </>
  );
};

// Schedule Pickup Form Component
const SchedulePickupForm = ({ onClose, darkMode }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    address: '',
    wasteType: '',
    pickupDate: '',
    pickupTime: '',
    specialInstructions: '',
    recurring: false,
    frequency: 'once'
  });

  const [currentStep, setCurrentStep] = useState(1);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const wasteTypes = [
    { value: 'general', label: 'General Waste', icon: 'üóëÔ∏è' },
    { value: 'recyclable', label: 'Recyclables', icon: '‚ôªÔ∏è' },
    { value: 'organic', label: 'Organic Waste', icon: 'üå±' },
    { value: 'hazardous', label: 'Hazardous', icon: '‚ö†Ô∏è' },
    { value: 'e-waste', label: 'E-Waste', icon: 'üîå' },
    { value: 'bulk', label: 'Bulk Items', icon: 'üõãÔ∏è' }
  ];

  const timeSlots = [
    '08:00 - 10:00',
    '10:00 - 12:00',
    '12:00 - 14:00',
    '14:00 - 16:00',
    '16:00 - 18:00'
  ];

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    
    // Simulate API call
    setTimeout(() => {
      setIsSubmitting(false);
      alert('üéâ Pickup scheduled successfully! You will receive a confirmation email shortly.');
      onClose();
    }, 2000);
  };

  const nextStep = () => setCurrentStep(prev => prev + 1);
  const prevStep = () => setCurrentStep(prev => prev - 1);

  const getMinDate = () => {
    const today = new Date();
    today.setDate(today.getDate() + 1); // Minimum tomorrow
    return today.toISOString().split('T')[0];
  };

  return (
    <motion.div 
      className="modal-overlay"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      onClick={onClose}
    >
      <motion.div 
        className={`schedule-form ${darkMode ? 'dark-mode' : ''}`}
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        exit={{ scale: 0.8, opacity: 0 }}
        transition={{ type: "spring", stiffness: 300, damping: 30 }}
        onClick={(e) => e.stopPropagation()}
      >
        {/* Header */}
        <div className="form-header">
          <h2>üìÖ Schedule a Pickup</h2>
          <p>Let us handle your waste disposal efficiently</p>
          <button className="close-btn" onClick={onClose}>‚úï</button>
        </div>

        {/* Progress Steps */}
        <div className="progress-steps">
          {[1, 2, 3].map(step => (
            <div key={step} className={`step ${step === currentStep ? 'active' : step < currentStep ? 'completed' : ''}`}>
              <div className="step-number">{step < currentStep ? '‚úì' : step}</div>
              <span className="step-label">
                {step === 1 && 'Personal Info'}
                {step === 2 && 'Pickup Details'}
                {step === 3 && 'Confirmation'}
              </span>
            </div>
          ))}
        </div>

        <form onSubmit={handleSubmit} className="schedule-form-content">
          {/* Step 1: Personal Information */}
          {currentStep === 1 && (
            <motion.div
              className="form-step"
              initial={{ x: 50, opacity: 0 }}
              animate={{ x: 0, opacity: 1 }}
              transition={{ duration: 0.3 }}
            >
              <h3>üë§ Your Information</h3>
              
              <div className="form-grid">
                <div className="input-group">
                  <label htmlFor="name">Full Name *</label>
                  <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleInputChange}
                    required
                    placeholder="Enter your full name"
                  />
                </div>

                <div className="input-group">
                  <label htmlFor="email">Email Address *</label>
                  <input
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    required
                    placeholder="your@email.com"
                  />
                </div>

                <div className="input-group">
                  <label htmlFor="phone">Phone Number *</label>
                  <input
                    type="tel"
                    id="phone"
                    name="phone"
                    value={formData.phone}
                    onChange={handleInputChange}
                    required
                    placeholder="+1 (555) 123-4567"
                  />
                </div>

                <div className="input-group full-width">
                  <label htmlFor="address">Pickup Address *</label>
                  <textarea
                    id="address"
                    name="address"
                    value={formData.address}
                    onChange={handleInputChange}
                    required
                    rows="3"
                    placeholder="Enter your complete address including apartment number, zip code, etc."
                  />
                </div>
              </div>

              <div className="form-actions">
                <button type="button" className="btn btn-secondary" onClick={onClose}>
                  Cancel
                </button>
                <button type="button" className="btn btn-primary" onClick={nextStep}>
                  Continue ‚Üí 
                </button>
              </div>
            </motion.div>
          )}

          {/* Step 2: Pickup Details */}
          {currentStep === 2 && (
            <motion.div
              className="form-step"
              initial={{ x: 50, opacity: 0 }}
              animate={{ x: 0, opacity: 1 }}
              transition={{ duration: 0.3 }}
            >
              <h3>üóìÔ∏è Pickup Details</h3>
              
              <div className="form-grid">
                <div className="input-group full-width">
                  <label>Waste Type *</label>
                  <div className="waste-type-grid">
                    {wasteTypes.map(type => (
                      <label key={type.value} className="waste-type-option">
                        <input
                          type="radio"
                          name="wasteType"
                          value={type.value}
                          checked={formData.wasteType === type.value}
                          onChange={handleInputChange}
                          required
                        />
                        <div className="waste-type-card">
                          <span className="waste-icon">{type.icon}</span>
                          <span>{type.label}</span>
                        </div>
                      </label>
                    ))}
                  </div>
                </div>

                <div className="input-group">
                  <label htmlFor="pickupDate">Preferred Date *</label>
                  <input
                    type="date"
                    id="pickupDate"
                    name="pickupDate"
                    value={formData.pickupDate}
                    onChange={handleInputChange}
                    required
                    min={getMinDate()}
                  />
                </div>

                <div className="input-group">
                  <label htmlFor="pickupTime">Time Slot *</label>
                  <select
                    id="pickupTime"
                    name="pickupTime"
                    value={formData.pickupTime}
                    onChange={handleInputChange}
                    required
                  >
                    <option value="">Select a time slot</option>
                    {timeSlots.map(slot => (
                      <option key={slot} value={slot}>{slot}</option>
                    ))}
                  </select>
                </div>

                <div className="input-group full-width">
                  <label className="checkbox-label">
                    <input
                      type="checkbox"
                      name="recurring"
                      checked={formData.recurring}
                      onChange={handleInputChange}
                    />
                    <span className="checkmark"></span>
                    Schedule recurring pickup
                  </label>
                  
                  {formData.recurring && (
                    <div className="recurring-options">
                      <select
                        name="frequency"
                        value={formData.frequency}
                        onChange={handleInputChange}
                      >
                        <option value="weekly">Weekly</option>
                        <option value="bi-weekly">Every 2 weeks</option>
                        <option value="monthly">Monthly</option>
                      </select>
                    </div>
                  )}
                </div>

                <div className="input-group full-width">
                  <label htmlFor="specialInstructions">Special Instructions</label>
                  <textarea
                    id="specialInstructions"
                    name="specialInstructions"
                    value={formData.specialInstructions}
                    onChange={handleInputChange}
                    rows="3"
                    placeholder="Any special instructions for our pickup team..."
                  />
                </div>
              </div>

              <div className="form-actions">
                <button type="button" className="btn btn-secondary" onClick={prevStep}>
                  ‚Üê Back
                </button>
                <button type="button" className="btn btn-primary" onClick={nextStep}>
                  Continue ‚Üí 
                </button>
              </div>
            </motion.div>
          )}

          {/* Step 3: Confirmation */}
          {currentStep === 3 && (
            <motion.div
              className="form-step"
              initial={{ x: 50, opacity: 0 }}
              animate={{ x: 0, opacity: 1 }}
              transition={{ duration: 0.3 }}
            >
              <h3>‚úÖ Confirm Your Pickup</h3>
              
              <div className="confirmation-details">
                <div className="detail-card">
                  <h4>Personal Information</h4>
                  <div className="detail-row">
                    <span>Name:</span>
                    <span>{formData.name}</span>
                  </div>
                  <div className="detail-row">
                    <span>Email:</span>
                    <span>{formData.email}</span>
                  </div>
                  <div className="detail-row">
                    <span>Phone:</span>
                    <span>{formData.phone}</span>
                  </div>
                  <div className="detail-row">
                    <span>Address:</span>
                    <span>{formData.address}</span>
                  </div>
                </div>

                <div className="detail-card">
                  <h4>Pickup Details</h4>
                  <div className="detail-row">
                    <span>Waste Type:</span>
                    <span>{wasteTypes.find(t => t.value === formData.wasteType)?.label}</span>
                  </div>
                  <div className="detail-row">
                    <span>Date:</span>
                    <span>{new Date(formData.pickupDate).toLocaleDateString()}</span>
                  </div>
                  <div className="detail-row">
                    <span>Time:</span>
                    <span>{formData.pickupTime}</span>
                  </div>
                  {formData.recurring && (
                    <div className="detail-row">
                      <span>Frequency:</span>
                      <span>{formData.frequency}</span>
                    </div>
                  )}
                  {formData.specialInstructions && (
                    <div className="detail-row">
                      <span>Instructions:</span>
                      <span>{formData.specialInstructions}</span>
                    </div>
                  )}
                </div>
              </div>

              <div className="form-notice">
                <div className="notice-icon">üí°</div>
                <p>
                  <strong>Important:</strong> Please ensure your waste is properly sorted and 
                  placed at the designated pickup location 30 minutes before the scheduled time.
                </p>
              </div>

              <div className="form-actions">
                <button type="button" className="btn btn-secondary" onClick={prevStep}>
                  ‚Üê Back
                </button>
                <button type="submit" className="btn btn-primary" disabled={isSubmitting}>
                  {isSubmitting ? (
                    <>
                      <div className="loading-spinner"></div>
                      Scheduling...
                    </>
                  ) : (
                    'Confirm Pickup üéâ'
                  )}
                </button>
              </div>
            </motion.div>
          )}
        </form>
      </motion.div>
    </motion.div>
  );
};

export default Header;
