import React, { useRef, useState, useCallback } from 'react';
import Webcam from 'react-webcam';
import { motion, AnimatePresence } from 'framer-motion';
import { analyzeImage } from '../../services/geminiService';
import './CameraInput.css';

const CameraInput = ({ onImageCapture, onImageUpload }) => {
  const webcamRef = useRef(null);
  const fileInputRef = useRef(null);
  const [imgSrc, setImgSrc] = useState(null);
  const [error, setError] = useState(null);
  const [isCapturing, setIsCapturing] = useState(false);

  const capture = useCallback(() => {
    if (!webcamRef.current) return;
    const imageSrc = webcamRef.current.getScreenshot({
      width: 1280,
      height: 720,
    });
    setImgSrc(imageSrc);
    setError(null);
  }, []);

  const triggerFileInput = () => {
    fileInputRef.current?.click();
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith('image/')) {
      const reader = new FileReader();
      reader.onloadend = async () => {
        setImgSrc(reader.result);
        setError(null);
        await handleAnalysis(reader.result);
      };
      reader.onerror = () => {
        setError('Failed to read image file');
      };
      reader.readAsDataURL(file);
    } else {
      setError('Please select a valid image file');
    }
  };

  const handleAnalysis = async (imageData) => {
    try {
      setError(null);
      const result = await analyzeImage(imageData);
      if (onImageCapture) {
        onImageCapture(result);
      }
    } catch (err) {
      setError('Failed to analyze image. Please try again.');
      console.error('Analysis error:', err);
    }
  };

  const retake = () => {
    setImgSrc(null);
    setError(null);
  };

  return (
    <div className="camera-input-wrapper">
      <AnimatePresence>
        {!imgSrc ? (
          <motion.div
            className="camera-input-panel"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
          >
            {/* Webcam live feed */}
            <Webcam
              audio={false}
              ref={webcamRef}
              screenshotFormat="image/jpeg"
              className="camera-webcam"
              videoConstraints={{ facingMode: 'environment' }}
              onUserMediaError={(e) => {
                setError('Camera access denied or unavailable');
                console.error(e);
              }}
            />

            {error && <div className="error-message">⚠️ {error}</div>}

            <div className="camera-controls">
              <button onClick={capture} className="btn btn-capture">
                Capture
              </button>
              <button onClick={triggerFileInput} className="btn btn-upload">
                Upload Image
              </button>
              <input
                ref={fileInputRef}
                type="file"
                accept="image/*"
                style={{ display: 'none' }}
                onChange={handleFileUpload}
              />
            </div>
          </motion.div>
        ) : (
          <motion.div
            className="image-preview"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
          >
            <img src={imgSrc} alt="Captured" className="preview-img" />
            <div className="preview-actions">
              <button onClick={retake} className="btn btn-secondary">
                Retake
              </button>
              <button
                className="btn btn-primary"
                onClick={() => handleAnalysis(imgSrc)}
              >
                Analyze
              </button>
            </div>
            {error && <div className="error-message">⚠️ {error}</div>}
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default CameraInput;
