import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { fadeInUp, staggerContainer } from '../../utils/animations';
import './Identification.css';

const Identification = ({ onNavigate, selectedLocale, onResult }) => {
  const [activeTab, setActiveTab] = useState('camera');
  const [searchQuery, setSearchQuery] = useState('');
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [cameraActive, setCameraActive] = useState(false);
  const [stream, setStream] = useState(null);
  const [cameraError, setCameraError] = useState('');
  
  const fileInputRef = useRef(null);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  // ‚úÖ FIXED: Start Camera with proper video handling
  const startCamera = async () => {
    try {
      setCameraError('');
      console.log('üé• Starting camera...');
      
      // Request camera access
      const mediaStream = await navigator.mediaDevices.getUserMedia({
        video: {
          facingMode: 'environment', // Try back camera first
          width: { ideal: 1280 },
          height: { ideal: 720 }
        },
        audio: false
      });
      
      console.log('‚úÖ Camera stream obtained');
      setStream(mediaStream);
      
      // Assign stream to video element
      if (videoRef.current) {
        videoRef.current.srcObject = mediaStream;
        
        // Wait for metadata to load
        videoRef.current.onloadedmetadata = () => {
          console.log('üìπ Video metadata loaded');
          videoRef.current.play()
            .then(() => {
              console.log('‚ñ∂Ô∏è Video playing');
              setCameraActive(true);
            })
            .catch(err => {
              console.error('‚ùå Video play error:', err);
              setCameraError('Unable to start video. Please try again.');
            });
        };
      }
    } catch (err) {
      console.error('‚ùå Camera error:', err);
      
      // Try fallback - front camera
      try {
        console.log('üîÑ Trying front camera...');
        const fallbackStream = await navigator.mediaDevices.getUserMedia({
          video: true,
          audio: false
        });
        
        setStream(fallbackStream);
        if (videoRef.current) {
          videoRef.current.srcObject = fallbackStream;
          videoRef.current.onloadedmetadata = () => {
            videoRef.current.play().then(() => {
              setCameraActive(true);
              setCameraError('');
            });
          };
        }
      } catch (fallbackErr) {
        setCameraError('Camera access denied. Please allow camera permissions in your browser settings.');
        console.error('‚ùå Fallback camera error:', fallbackErr);
      }
    }
  };

  // ‚úÖ Stop Camera
  const stopCamera = () => {
    console.log('üõë Stopping camera...');
    if (stream) {
      stream.getTracks().forEach(track => {
        track.stop();
        console.log('‚èπÔ∏è Track stopped');
      });
      setStream(null);
    }
    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }
    setCameraActive(false);
    setCameraError('');
  };

  // ‚úÖ Capture Photo from video
  const capturePhoto = () => {
    console.log('üì∏ Capturing photo...');
    if (videoRef.current && canvasRef.current) {
      const video = videoRef.current;
      const canvas = canvasRef.current;
      
      // Set canvas dimensions to video dimensions
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;
      
      // Draw video frame to canvas
      const context = canvas.getContext('2d');
      context.drawImage(video, 0, 0, canvas.width, canvas.height);
      
      // Convert to data URL
      const dataUrl = canvas.toDataURL('image/jpeg', 0.9);
      console.log('‚úÖ Photo captured');
      
      setImagePreview(dataUrl);
      setSelectedImage(dataUrl);
      stopCamera(); // Stop camera after capture
    }
  };

  // ‚úÖ Handle file upload
  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      console.log('üìÅ File selected:', file.name);
      setSelectedImage(file);
      const reader = new FileReader();
      reader.onload = (e) => {
        setImagePreview(e.target.result);
        console.log('‚úÖ Image loaded');
      };
      reader.readAsDataURL(file);
    }
  };

  // ‚úÖ Analyze Image
  const handleImageAnalysis = () => {
    if (!selectedImage) {
      alert('‚ö†Ô∏è Please capture or select an image first');
      return;
    }
    
    console.log('üîç Starting analysis...');
    setIsAnalyzing(true);
    
    // Simulate AI analysis
    setTimeout(() => {
      const mockResult = {
        id: 'analyzed-item',
        name: 'Plastic Bottle',
        material: 'plastic',
        category: 'Recyclable',
        disposal: 'Recycle in blue bin',
        tips: [
          'Rinse thoroughly before recycling',
          'Remove cap and label if possible',
          'Flatten to save space in recycling bin',
          'Check recycling number on bottom'
        ],
        confidence: '94%',
        binColor: 'blue'
      };
      
      setIsAnalyzing(false);
      console.log('‚úÖ Analysis complete:', mockResult);
      
      // Pass result to parent
      if (typeof onResult === 'function') {
        onResult(mockResult);
      }
      
      // Navigate to results
      if (typeof onNavigate === 'function') {
        onNavigate('results');
      }
    }, 2500);
  };

  // ‚úÖ Text Search
  const handleTextSearch = (e) => {
    e.preventDefault();
    if (!searchQuery.trim()) return;
    
    console.log('üîç Searching for:', searchQuery);
    setIsAnalyzing(true);
    
    setTimeout(() => {
      const query = searchQuery.toLowerCase();
      let mockResult = {
        id: 'searched-item',
        name: searchQuery,
        material: 'mixed',
        category: 'General Waste',
        disposal: 'Check local guidelines',
        tips: ['Clean before disposal', 'Separate materials if possible'],
        confidence: '88%',
        binColor: 'gray'
      };

      // Smart categorization
      if (query.includes('plastic') || query.includes('bottle')) {
        mockResult = {
          ...mockResult,
          material: 'plastic',
          category: 'Recyclable',
          disposal: 'Recycle in blue bin',
          tips: ['Rinse clean', 'Remove caps', 'Flatten if possible', 'Check recycling number'],
          binColor: 'blue'
        };
      } else if (query.includes('glass') || query.includes('jar')) {
        mockResult = {
          ...mockResult,
          material: 'glass',
          category: 'Recyclable',
          disposal: 'Recycle in green bin',
          tips: ['Rinse clean', 'Remove lids', 'No broken glass', 'Sort by color if required'],
          binColor: 'green'
        };
      } else if (query.includes('metal') || query.includes('can') || query.includes('aluminum')) {
        mockResult = {
          ...mockResult,
          material: 'metal',
          category: 'Recyclable',
          disposal: 'Recycle in blue bin',
          tips: ['Rinse clean', 'Crush to save space', 'Remove labels', 'Check for recycling acceptance'],
          binColor: 'blue'
        };
      } else if (query.includes('paper') || query.includes('cardboard')) {
        mockResult = {
          ...mockResult,
          material: 'paper',
          category: 'Recyclable',
          disposal: 'Recycle in blue bin',
          tips: ['Keep dry', 'Flatten boxes', 'Remove plastic tape', 'No greasy paper'],
          binColor: 'blue'
        };
      } else if (query.includes('food') || query.includes('organic')) {
        mockResult = {
          ...mockResult,
          material: 'organic',
          category: 'Compostable',
          disposal: 'Compost in green bin',
          tips: ['No plastic bags', 'Use compostable liner', 'Keep lid closed', 'No meat/dairy in some areas'],
          binColor: 'green'
        };
      } else if (query.includes('battery') || query.includes('electronic')) {
        mockResult = {
          ...mockResult,
          material: 'hazardous',
          category: 'Special Waste',
          disposal: 'Take to special collection point',
          tips: ['Never put in regular bins', 'Find local drop-off', 'Tape battery terminals', 'Store safely'],
          binColor: 'red'
        };
      }

      setIsAnalyzing(false);
      
      if (onResult) onResult(mockResult);
      if (onNavigate) onNavigate('results');
    }, 1800);
  };

  // Quick Items
  const quickItems = [
    { name: 'Plastic Bottle', icon: 'üß¥', id: 'plastic-bottle', color: '#2196F3' },
    { name: 'Glass Jar', icon: 'üç∂', id: 'glass-jar', color: '#4CAF50' },
    { name: 'Aluminum Can', icon: 'ü•´', id: 'aluminum-can', color: '#FF9800' },
    { name: 'Newspaper', icon: 'üì∞', id: 'newspaper', color: '#795548' },
    { name: 'Cardboard', icon: 'üì¶', id: 'cardboard', color: '#8BC34A' },
    { name: 'Battery', icon: 'üîã', id: 'battery', color: '#F44336' },
  ];

  const handleQuickItemSelect = (itemId) => {
    console.log('‚ö° Quick select:', itemId);
    setIsAnalyzing(true);
    
    setTimeout(() => {
      const item = quickItems.find(i => i.id === itemId);
      const resultsMap = {
        'plastic-bottle': {
          material: 'plastic',
          category: 'Recyclable',
          disposal: 'Recycle in blue bin',
          tips: ['Rinse thoroughly', 'Remove cap', 'Flatten bottle', 'Check recycling number'],
          binColor: 'blue',
          confidence: '96%'
        },
        'glass-jar': {
          material: 'glass',
          category: 'Recyclable',
          disposal: 'Recycle in green bin',
          tips: ['Rinse clean', 'Remove metal lid', 'No broken glass', 'Sort by color'],
          binColor: 'green',
          confidence: '98%'
        },
        'aluminum-can': {
          material: 'metal',
          category: 'Recyclable',
          disposal: 'Recycle in blue bin',
          tips: ['Rinse clean', 'Crush can', 'Remove paper labels', '100% recyclable'],
          binColor: 'blue',
          confidence: '99%'
        },
        'newspaper': {
          material: 'paper',
          category: 'Recyclable',
          disposal: 'Recycle in blue bin',
          tips: ['Keep dry', 'Bundle together', 'Remove plastic wrap', 'No wet paper'],
          binColor: 'blue',
          confidence: '97%'
        },
        'cardboard': {
          material: 'paper',
          category: 'Recyclable',
          disposal: 'Recycle in blue bin',
          tips: ['Flatten boxes', 'Remove tape', 'Keep dry', 'Break down large boxes'],
          binColor: 'blue',
          confidence: '95%'
        },
        'battery': {
          material: 'hazardous',
          category: 'Special Waste',
          disposal: 'Take to collection point',
          tips: ['Never put in bins', 'Find drop-off location', 'Tape terminals', 'Handle with care'],
          binColor: 'red',
          confidence: '100%'
        }
      };

      const result = resultsMap[itemId] || {};
      const mockResult = {
        id: itemId,
        name: item.name,
        ...result
      };

      setIsAnalyzing(false);
      if (onResult) onResult(mockResult);
      if (onNavigate) onNavigate('results');
    }, 1200);
  };

  // ‚úÖ Back to Home
  const handleBackToHome = () => {
    console.log('üè† Navigating back to home');
    stopCamera();
    setImagePreview(null);
    setSelectedImage(null);
    if (onNavigate) onNavigate('home');
  };

  const triggerFileInput = () => {
    fileInputRef.current?.click();
  };

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (stream) {
        stream.getTracks().forEach(track => track.stop());
      }
    };
  }, [stream]);

  return (
    <div className="identification-page">
      <div className="container">
        {/* Back Button */}
        <motion.button
          className="back-btn"
          onClick={handleBackToHome}
          whileHover={{ x: -5 }}
          whileTap={{ scale: 0.95 }}
        >
          ‚Üê Back to Home
        </motion.button>

        {/* Header */}
        <motion.div
          className="page-header"
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
        >
          <h1>Waste Identification</h1>
          <p>Choose your method to identify and dispose correctly</p>
          {selectedLocale && (
            <div className="locale-badge">
              üìç {selectedLocale}
            </div>
          )}
        </motion.div>

        {/* Analyzing Overlay */}
        <AnimatePresence>
          {isAnalyzing && (
            <motion.div
              className="analyzing-overlay"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
            >
              <div className="analyzing-content">
                <div className="spinner"></div>
                <h3>Analyzing...</h3>
                <p>AI is identifying the best disposal method</p>
              </div>
            </motion.div>
          )}
        </AnimatePresence>

        {/* Tabs */}
        <div className="analysis-options">
          <div className="tabs">
            <motion.button
              className={`tab ${activeTab === 'camera' ? 'active' : ''}`}
              onClick={() => setActiveTab('camera')}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
            >
              üì∑ Camera
            </motion.button>
            <motion.button
              className={`tab ${activeTab === 'upload' ? 'active' : ''}`}
              onClick={() => setActiveTab('upload')}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
            >
              üñºÔ∏è Upload
            </motion.button>
            <motion.button
              className={`tab ${activeTab === 'search' ? 'active' : ''}`}
              onClick={() => setActiveTab('search')}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
            >
              üîç Search
            </motion.button>
            <motion.button
              className={`tab ${activeTab === 'quick' ? 'active' : ''}`}
              onClick={() => setActiveTab('quick')}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
            >
              ‚ö° Quick Select
            </motion.button>
          </div>

          {/* Tab Content */}
          <div className="tab-content">
            {/* ‚úÖ CAMERA TAB - WORKING */}
            {activeTab === 'camera' && (
              <motion.div
                className="camera-section"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
              >
                <p className="section-description">
                  Take a photo using your device camera
                </p>
                
                {cameraError && (
                  <div className="camera-error">
                    ‚ö†Ô∏è {cameraError}
                  </div>
                )}
                
                <div className="camera-container">
                  {!cameraActive && !imagePreview && (
                    <motion.button
                      className="btn btn-primary btn-large"
                      onClick={startCamera}
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                    >
                      üì∏ Start Camera
                    </motion.button>
                  )}

                  {cameraActive && (
                    <div className="video-wrapper">
                      <video
                        ref={videoRef}
                        autoPlay
                        playsInline
                        muted
                        className="camera-video"
                      />
                      <div className="camera-controls">
                        <motion.button
                          className="btn btn-primary"
                          onClick={capturePhoto}
                          whileHover={{ scale: 1.05 }}
                          whileTap={{ scale: 0.95 }}
                        >
                          üì∏ Capture Photo
                        </motion.button>
                        <motion.button
                          className="btn btn-secondary"
                          onClick={stopCamera}
                          whileHover={{ scale: 1.05 }}
                          whileTap={{ scale: 0.95 }}
                        >
                          ‚ùå Cancel
                        </motion.button>
                      </div>
                    </div>
                  )}

                  {imagePreview && !cameraActive && (
                    <div className="preview-container">
                      <img src={imagePreview} alt="Captured" className="preview-image" />
                      <div className="preview-actions">
                        <motion.button
                          className="btn btn-primary"
                          onClick={handleImageAnalysis}
                          whileHover={{ scale: 1.05 }}
                          whileTap={{ scale: 0.95 }}
                        >
                          üîç Analyze Image
                        </motion.button>
                        <motion.button
                          className="btn btn-secondary"
                          onClick={() => {
                            setImagePreview(null);
                            setSelectedImage(null);
                          }}
                          whileHover={{ scale: 1.05 }}
                          whileTap={{ scale: 0.95 }}
                        >
                          üîÑ Retake
                        </motion.button>
                      </div>
                    </div>
                  )}
                  
                  <canvas ref={canvasRef} style={{ display: 'none' }} />
                </div>
              </motion.div>
            )}

            {/* Upload Tab */}
            {activeTab === 'upload' && (
              <motion.div className="upload-section" initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
                <p className="section-description">Choose an image from your device</p>
                
                <input
                  type="file"
                  ref={fileInputRef}
                  onChange={handleImageUpload}
                  accept="image/*"
                  style={{ display: 'none' }}
                />

                {!imagePreview && (
                  <motion.div className="upload-area" onClick={triggerFileInput} whileHover={{ scale: 1.02 }}>
                    <div className="upload-icon">üìÅ</div>
                    <p>Click to select an image</p>
                    <span>or drag and drop here</span>
                  </motion.div>
                )}

                {imagePreview && (
                  <div className="preview-container">
                    <img src={imagePreview} alt="Uploaded" className="preview-image" />
                    <div className="preview-actions">
                      <motion.button className="btn btn-primary" onClick={handleImageAnalysis} whileHover={{ scale: 1.05 }}>
                        üîç Analyze Image
                      </motion.button>
                      <motion.button className="btn btn-secondary" onClick={() => { setImagePreview(null); setSelectedImage(null); }} whileHover={{ scale: 1.05 }}>
                        üîÑ Choose Another
                      </motion.button>
                    </div>
                  </div>
                )}
              </motion.div>
            )}

            {/* Search Tab */}
            {activeTab === 'search' && (
              <motion.div className="search-section" initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
                <p className="section-description">Type the item name to dispose</p>
                <form onSubmit={handleTextSearch} className="search-form">
                  <input
                    type="text"
                    placeholder="e.g., Plastic bottle, Glass jar, Battery..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="search-input"
                  />
                  <motion.button type="submit" className="btn btn-primary" whileHover={{ scale: 1.05 }}>
                    üîç Search
                  </motion.button>
                </form>
              </motion.div>
            )}

            {/* Quick Select Tab */}
            {activeTab === 'quick' && (
              <motion.div className="quick-section" initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
                <p className="section-description">Select an item for instant guidance</p>
                <div className="quick-items-grid">
                  {quickItems.map((item, index) => (
                    <motion.div
                      key={item.id}
                      className="quick-item-card"
                      onClick={() => handleQuickItemSelect(item.id)}
                      initial={{ opacity: 0, y: 20 }}
                      animate={{ opacity: 1, y: 0 }}
                      transition={{ delay: index * 0.1 }}
                      whileHover={{ scale: 1.05, y: -5 }}
                      whileTap={{ scale: 0.95 }}
                      style={{ '--item-color': item.color }}
                    >
                      <div className="quick-item-icon">{item.icon}</div>
                      <div className="quick-item-name">{item.name}</div>
                    </motion.div>
                  ))}
                </div>
              </motion.div>
            )}
          </div>
        </div>

        {/* Tips */}
        <motion.div className="tips-section" initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} transition={{ delay: 0.5 }}>
          <h3>üí° Tips for Better Results</h3>
          <div className="tips-grid">
            <motion.div className="tip-card" whileHover={{ y: -5 }}>
              <span className="tip-icon">üì∏</span>
              <p>Take photos in good lighting with clear view</p>
            </motion.div>
            <motion.div className="tip-card" whileHover={{ y: -5 }}>
              <span className="tip-icon">üßº</span>
              <p>Clean and dry items for accurate recycling</p>
            </motion.div>
            <motion.div className="tip-card" whileHover={{ y: -5 }}>
              <span className="tip-icon">üîç</span>
              <p>Look for recycling symbols and material info</p>
            </motion.div>
            <motion.div className="tip-card" whileHover={{ y: -5 }}>
              <span className="tip-icon">üìù</span>
              <p>Use detailed descriptions when searching</p>
            </motion.div>
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default Identification;
