import React, { useRef, useState, useEffect, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import './CameraInput.css';

const CameraInput = ({ onImageCapture, onImageUpload }) => {
  const fileInputRef = useRef(null);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [stream, setStream] = useState(null);
  const [isCameraActive, setIsCameraActive] = useState(false);
  const [previewImage, setPreviewImage] = useState(null);
  const [isDragging, setIsDragging] = useState(false);
  const [cameraError, setCameraError] = useState(null);

  // Handle camera stream when camera is active
  useEffect(() => {
    if (isCameraActive && stream && videoRef.current) {
      videoRef.current.srcObject = stream;
    }

    // Cleanup: stop camera when component unmounts or camera becomes inactive
    return () => {
      if (stream) {
        stream.getTracks().forEach(track => track.stop());
      }
    };
  }, [isCameraActive, stream]);

  const processFile = (file) => {
    if (file && file.type.startsWith('image/')) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setPreviewImage(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    processFile(file);
  };

  const handleDragEnter = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(true);
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(false);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.stopPropagation();
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(false);
    const file = e.dataTransfer.files[0];
    processFile(file);
  };

  const handleAnalyze = () => {
    if (previewImage) {
      onImageUpload(previewImage);
      setPreviewImage(null);
    }
  };

  const startCamera = async () => {
    try {
      setCameraError(null);
      const mediaStream = await navigator.mediaDevices.getUserMedia({
        video: { 
          facingMode: { ideal: 'environment' },
          width: { ideal: 1280 },
          height: { ideal: 720 }
        }
      });
      setStream(mediaStream);
      setIsCameraActive(true);
    } catch (error) {
      console.error('Error accessing camera:', error);
      setCameraError('Unable to access camera. Please check permissions.');
    }
  };

  const stopCamera = () => {
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
      setStream(null);
    }
    setIsCameraActive(false);
    setCameraError(null);
  };

  const captureImage = useCallback(() => {
    if (videoRef.current && canvasRef.current) {
      const video = videoRef.current;
      const canvas = canvasRef.current;
      
      // Set canvas dimensions to match video
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;
      
      // Draw video frame to canvas
      const context = canvas.getContext('2d');
      context.drawImage(video, 0, 0, canvas.width, canvas.height);
      
      // Convert canvas to image data URL
      const imageSrc = canvas.toDataURL('image/jpeg', 0.95);
      
      if (imageSrc) {
        onImageCapture(imageSrc);
        stopCamera();
      }
    }
  }, [onImageCapture]);

  const panelVariants = {
    hidden: { opacity: 0, scale: 0.9 },
    visible: { opacity: 1, scale: 1, transition: { duration: 0.4 } },
    exit: { opacity: 0, scale: 0.9, transition: { duration: 0.3 } },
  };

  const cameraVariants = {
    hidden: { opacity: 0, scale: 1.1 },
    visible: { opacity: 1, scale: 1, transition: { duration: 0.4 } },
    exit: { opacity: 0, scale: 1.1, transition: { duration: 0.3 } },
  };

  return (
    <div className="camera-input-wrapper">
      {/* Hidden canvas for capturing images */}
      <canvas ref={canvasRef} style={{ display: 'none' }} />
      
      <AnimatePresence>
        {!isCameraActive && !previewImage && (
          <motion.div
            className="camera-input-panel"
            variants={panelVariants}
            initial="hidden"
            animate="visible"
            exit="exit"
          >
            <div className="camera-input-header">
              <h2>ðŸ“¸ Waste Identification</h2>
              <p>Use your camera to identify waste in seconds.</p>
            </div>

            <motion.div
              className={`drag-drop-area ${isDragging ? 'dragging' : ''}`}
              onDragEnter={handleDragEnter}
              onDragLeave={handleDragLeave}
              onDragOver={handleDragOver}
              onDrop={handleDrop}
              onClick={() => fileInputRef.current.click()}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
            >
              <div className="upload-icon">ðŸ“¤</div>
              <p className="drag-text">
                <strong>Drag & drop an image here</strong>
              </p>
              <p className="or-text">or click to browse</p>
              <input
                ref={fileInputRef}
                type="file"
                accept="image/*"
                onChange={handleFileUpload}
                style={{ display: 'none' }}
              />
            </motion.div>

            <div className="divider">
              <span>OR</span>
            </div>

            <motion.button
              className="btn btn-camera"
              onClick={startCamera}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <span className="icon">ðŸ“·</span> Open Camera
            </motion.button>

            {cameraError && (
              <div className="error-message" style={{ marginTop: '1rem', color: '#f44336', textAlign: 'center' }}>
                {cameraError}
              </div>
            )}
          </motion.div>
        )}

        {isCameraActive && (
          <motion.div
            className="camera-view"
            variants={cameraVariants}
            initial="hidden"
            animate="visible"
            exit="exit"
          >
            <div className="camera-container">
              <video
                ref={videoRef}
                autoPlay
                playsInline
                className="camera-video"
              />
              <div className="camera-overlay">
                <div className="focus-frame"></div>
              </div>
            </div>
            <div className="camera-controls">
              <motion.button
                className="btn btn-secondary"
                onClick={stopCamera}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                âœ• Close
              </motion.button>
              <motion.button
                className="btn btn-capture"
                onClick={captureImage}
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
              >
                <span className="capture-ring"></span>
              </motion.button>
              <div style={{ width: '80px' }}></div>
            </div>
          </motion.div>
        )}

        {previewImage && (
          <motion.div
            className="image-preview"
            variants={panelVariants}
            initial="hidden"
            animate="visible"
            exit="exit"
          >
            <h3>Preview</h3>
            <img src={previewImage} alt="Preview" className="preview-img" />
            <div className="preview-actions">
              <motion.button
                className="btn btn-secondary"
                onClick={() => setPreviewImage(null)}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                âœ• Cancel
              </motion.button>
              <motion.button
                className="btn btn-primary"
                onClick={handleAnalyze}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                âœ“ Analyze
              </motion.button>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default CameraInput;
