import React from 'react';
import { motion } from 'framer-motion';
import './Footer.css';

const Footer = ({ onNavigate }) => {
  return (
    <footer className="footer">
      <div className="container">
        <div className="footer-content">
          <motion.div 
            className="footer-section"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
          >
            <h3>Smart Recycle Identifier</h3>
            <p>Making sustainable choices easy for everyone. Together, we can reduce contamination and promote recycling.</p>
          </motion.div>

          <motion.div 
            className="footer-section"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.1 }}
          >
            <h4>Quick Links</h4>
            <ul>
              <li><button onClick={() => onNavigate('home')}>Home</button></li>
              <li><button onClick={() => onNavigate('identification')}>Identify Waste</button></li>
              <li><button onClick={() => onNavigate('local-rules')}>Local Rules</button></li>
              <li><button onClick={() => onNavigate('upcycling')}>Upcycling Ideas</button></li>
            </ul>
          </motion.div>

          <motion.div 
            className="footer-section"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.2 }}
          >
            <h4>Contact</h4>
            <p>📧 support@smartrecycle.com</p>
            <p>📞 91+ 9117204120</p>
            <div className="social-links">
              <motion.a href="#" whileHover={{ scale: 1.2 }}>📘</motion.a>
              <motion.a href="#" whileHover={{ scale: 1.2 }}>🐦</motion.a>
              <motion.a href="#" whileHover={{ scale: 1.2 }}>📷</motion.a>
              <motion.a href="#" whileHover={{ scale: 1.2 }}>💼</motion.a>
            </div>
          </motion.div>
        </div>

        <motion.div 
          className="footer-bottom"
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          transition={{ duration: 0.5, delay: 0.3 }}
        >
          <p>&copy; 2025 Smart Recycle Identifier.Created by Poonam patil, Making the world greener, one item at a time.</p>
        </motion.div>
      </div>
    </footer>
  );
};

export default Footer;
