import React, { useRef, useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import './CameraInput.css';

const CameraInput = ({ onImageCapture, onImageUpload }) => {
  const fileInputRef = useRef(null);
  const videoRef = useRef(null);
  const [stream, setStream] = useState(null);
  const [isCameraActive, setIsCameraActive] = useState(false);
  const [previewImage, setPreviewImage] = useState(null);
  const [isDragging, setIsDragging] = useState(false);

  // KEY FIX: This useEffect handles attaching the stream to the video element.
  // It runs only when the camera becomes active, ensuring the video element exists.
  useEffect(() => {
    if (isCameraActive && stream && videoRef.current) {
      videoRef.current.srcObject = stream;
    }
    
    // Cleanup function: This will stop the camera when the component unmounts
    // or when isCameraActive/stream changes, preventing the camera from staying on.
    return () => {
      if (stream) {
        stream.getTracks().forEach(track => track.stop());
      }
    };
  }, [isCameraActive, stream]);
  
  const processFile = (file) => {
    if (file && file.type.startsWith('image/')) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setPreviewImage(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    processFile(file);
  };

  const handleDragEnter = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(true);
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(false);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.stopPropagation();
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(false);
    const file = e.dataTransfer.files[0];
    processFile(file);
  };
  
  const handleAnalyze = () => {
    if (previewImage) {
      onImageUpload(previewImage);
      setPreviewImage(null);
    }
  };

  const startCamera = async () => {
    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({ 
        video: { facingMode: { ideal: 'environment' } } 
      });
      // Just update the state. The useEffect will handle the rest.
      setStream(mediaStream);
      setIsCameraActive(true);
    } catch (error) {
      console.error('Error accessing camera:', error);
      if (error.name === 'NotAllowedError' || error.name === 'PermissionDeniedError') {
        alert('Camera permission denied. Please allow camera access in your browser settings and refresh the page.');
      } else if (error.name === 'NotFoundError' || error.name === 'DevicesNotFoundError') {
        alert('No camera found. Please ensure a camera is connected and enabled.');
      } else {
        alert(`Could not access the camera: ${error.message}. Please check permissions and ensure it is not in use by another application.`);
      }
    }
  };

  const stopCamera = () => {
    // The useEffect's cleanup function will handle stopping the tracks.
    setStream(null);
    setIsCameraActive(false);
  };

  const captureImage = () => {
    if (videoRef.current && videoRef.current.srcObject) {
      const canvas = document.createElement('canvas');
      canvas.width = videoRef.current.videoWidth;
      canvas.height = videoRef.current.videoHeight;
      const context = canvas.getContext('2d');
      context.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);
      const imageData = canvas.toDataURL('image/jpeg');
      onImageCapture(imageData);
      stopCamera();
    }
  };
  
  const panelVariants = {
    hidden: { opacity: 0, scale: 0.9 },
    visible: { opacity: 1, scale: 1, transition: { duration: 0.4 } },
    exit: { opacity: 0, scale: 0.9, transition: { duration: 0.3 } },
  };

  const cameraVariants = {
    hidden: { opacity: 0, scale: 1.1 },
    visible: { opacity: 1, scale: 1, transition: { duration: 0.4 } },
    exit: { opacity: 0, scale: 1.1, transition: { duration: 0.3 } },
  };

  return (
    <div className="camera-input-container">
      <AnimatePresence mode="wait">
        {!isCameraActive ? (
          previewImage ? (
            <motion.div
              key="preview"
              className="image-preview-container"
              variants={panelVariants}
              initial="hidden"
              animate="visible"
              exit="exit"
            >
              <img src={previewImage} alt="Preview" className="preview-image" />
              <div className="preview-actions">
                <motion.button 
                  className="btn btn-primary"
                  onClick={handleAnalyze}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  Analyze Image
                </motion.button>
                <motion.button 
                  className="btn btn-secondary"
                  onClick={() => setPreviewImage(null)}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  Clear
                </motion.button>
              </div>
            </motion.div>
          ) : (
            <motion.div
              key="panel"
              className="camera-input-panel"
              variants={panelVariants}
              initial="hidden"
              animate="visible"
              exit="exit"
            >
              <div className="panel-icon">ðŸ“·</div>
              <h3 className="panel-title">Point, Scan, & Sort</h3>
              <p className="panel-description">Use your camera to identify waste in seconds.</p>
              <div className="panel-actions">
                <motion.button
                  className="btn btn-primary btn-large"
                  onClick={startCamera}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  Scan with Camera
                </motion.button>
              </div>
              <div 
                className={`drop-zone ${isDragging ? 'dragging' : ''}`}
                onClick={() => fileInputRef.current?.click()}
                onDragEnter={handleDragEnter}
                onDragLeave={handleDragLeave}
                onDragOver={handleDragOver}
                onDrop={handleDrop}
              >
                <span className="drop-zone-icon">ðŸ“¤</span>
                <p className="drop-zone-text">
                  <strong>Drag & drop an image here</strong>
                  <br/> or click to browse
                </p>
              </div>
            </motion.div>
          )
        ) : (
          <motion.div
            key="camera"
            className="camera-preview"
            variants={cameraVariants}
            initial="hidden"
            animate="visible"
            exit="exit"
          >
            <video ref={videoRef} autoPlay playsInline className="camera-video" />
            <div className="camera-viewfinder"></div>
            <div className="camera-controls">
              <motion.button
                className="control-btn cancel-btn"
                onClick={stopCamera}
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
              >
                âœ•
              </motion.button>
              <motion.button
                className="shutter-btn"
                onClick={captureImage}
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
              >
                <div className="shutter-inner"></div>
              </motion.button>
              <motion.button
                className="control-btn flip-btn"
                whileHover={{ scale: 1.1, rotate: 180 }}
                whileTap={{ scale: 0.9 }}
                title="Flip camera (feature coming soon)"
                disabled
              >
                ðŸ”„
              </motion.button>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
      <input
        type="file"
        ref={fileInputRef}
        accept="image/*"
        onChange={handleFileUpload}
        style={{ display: 'none' }}
      />
    </div>
  );
};

export default CameraInput;
