import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { fadeInUp, staggerContainer } from '../../utils/animations';
import './Identification.css';

const Identification = ({ onNavigate, selectedLocale, onResult }) => {
  const [activeTab, setActiveTab] = useState('camera');
  const [searchQuery, setSearchQuery] = useState('');
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [cameraActive, setCameraActive] = useState(false);
  const [stream, setStream] = useState(null);
  const [cameraError, setCameraError] = useState('');
  
  const fileInputRef = useRef(null);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  // FIXED: Camera function with proper video loading
  const startCamera = async () => {
    try {
      setCameraError('');
      console.log('Starting camera...');
      
      const mediaStream = await navigator.mediaDevices.getUserMedia({
        video: {
          facingMode: 'environment',
          width: { ideal: 1280 },
          height: { ideal: 720 }
        },
        audio: false
      });
      
      setStream(mediaStream);
      
      // CRITICAL FIX: Properly assign stream to video element
      if (videoRef.current) {
        videoRef.current.srcObject = mediaStream;
        
        // Wait for video metadata to load before showing
        videoRef.current.onloadedmetadata = () => {
          videoRef.current.play().then(() => {
            console.log('Camera started successfully');
            setCameraActive(true);
          }).catch(err => {
            console.error('Video play error:', err);
            setCameraError('Error starting video preview');
          });
        };
      }
    } catch (err) {
      console.error('Camera access error:', err);
      setCameraError('Unable to access camera. Please check permissions.');
      
      // Fallback: try user-facing camera
      try {
        const fallbackStream = await navigator.mediaDevices.getUserMedia({
          video: true,
          audio: false
        });
        
        setStream(fallbackStream);
        
        if (videoRef.current) {
          videoRef.current.srcObject = fallbackStream;
          videoRef.current.onloadedmetadata = () => {
            videoRef.current.play().then(() => {
              setCameraActive(true);
              setCameraError('');
            });
          };
        }
      } catch (fallbackErr) {
        console.error('Fallback camera error:', fallbackErr);
        setCameraError('Camera access denied. Please allow camera permissions.');
      }
    }
  };

  const stopCamera = () => {
    console.log('Stopping camera...');
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
      setStream(null);
    }
    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }
    setCameraActive(false);
    setCameraError('');
  };

  const capturePhoto = () => {
    if (videoRef.current && canvasRef.current) {
      const context = canvasRef.current.getContext('2d');
      canvasRef.current.width = videoRef.current.videoWidth;
      canvasRef.current.height = videoRef.current.videoHeight;
      context.drawImage(videoRef.current, 0, 0);
      
      const dataUrl = canvasRef.current.toDataURL('image/jpeg');
      setImagePreview(dataUrl);
      setSelectedImage(dataUrl);
      stopCamera();
    }
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedImage(file);
      const reader = new FileReader();
      reader.onload = (e) => {
        setImagePreview(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  // FIXED: Analysis with proper result handling
  const analyzeItem = (itemData) => {
    setIsAnalyzing(true);
    
    setTimeout(() => {
      setIsAnalyzing(false);
      console.log('Analysis complete:', itemData);
      
      // Call result callback
      if (typeof onResult === 'function') {
        onResult(itemData);
      }
      
      // Navigate to results page
      if (typeof onNavigate === 'function') {
        onNavigate('results');
      }
    }, 2000);
  };

  const handleImageAnalysis = () => {
    if (!selectedImage) {
      alert('Please select or capture an image first');
      return;
    }
    
    const mockResult = {
      id: 'uploaded-item',
      name: 'Plastic Bottle',
      material: 'plastic',
      category: 'Recyclable',
      disposal: 'Recycle in blue bin',
      tips: [
        'Rinse before recycling',
        'Remove caps if separate material',
        'Flatten to save space'
      ],
      confidence: '92%',
      binColor: 'blue'
    };
    
    analyzeItem(mockResult);
  };

  const handleTextSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      const query = searchQuery.toLowerCase();
      let material = 'mixed';
      let category = 'General Waste';
      let disposal = 'Check local guidelines';
      let tips = ['Clean before disposal', 'Separate different materials'];
      let binColor = 'gray';
      let name = searchQuery;

      if (query.includes('plastic') || query.includes('bottle')) {
        material = 'plastic';
        category = 'Recyclable';
        disposal = 'Recycle in blue bin';
        tips = ['Rinse thoroughly', 'Remove caps', 'Flatten if possible'];
        binColor = 'blue';
      } else if (query.includes('glass') || query.includes('jar')) {
        material = 'glass';
        category = 'Recyclable';
        disposal = 'Recycle in green bin';
        tips = ['Rinse clean', 'Remove lids', 'No broken glass in recycling'];
        binColor = 'green';
      } else if (query.includes('metal') || query.includes('can')) {
        material = 'metal';
        category = 'Recyclable';
        disposal = 'Recycle in blue bin';
        tips = ['Rinse clean', 'Remove labels', 'Crush to save space'];
        binColor = 'blue';
      } else if (query.includes('paper') || query.includes('cardboard')) {
        material = 'paper';
        category = 'Recyclable';
        disposal = 'Recycle in blue bin';
        tips = ['Keep dry', 'Flatten boxes', 'Remove plastic'];
        binColor = 'blue';
      } else if (query.includes('food') || query.includes('organic')) {
        material = 'organic';
        category = 'Compostable';
        disposal = 'Compost in green bin';
        tips = ['No plastic', 'Use compostable bags', 'Keep lid closed'];
        binColor = 'green';
      } else if (query.includes('battery')) {
        material = 'hazardous';
        category = 'Special Waste';
        disposal = 'Special disposal required';
        tips = ['Do not put in bins', 'Take to collection point', 'Tape terminals'];
        binColor = 'red';
      }

      const mockResult = {
        id: 'searched-item',
        name: name,
        material: material,
        category: category,
        disposal: disposal,
        tips: tips,
        confidence: '90%',
        binColor: binColor
      };

      analyzeItem(mockResult);
    }
  };

  const quickItems = [
    { name: 'Plastic Bottle', icon: '🧴', id: 'plastic-bottle', color: '#2196F3' },
    { name: 'Glass Jar', icon: '🍶', id: 'glass-jar', color: '#4CAF50' },
    { name: 'Aluminum Can', icon: '🥫', id: 'aluminum-can', color: '#FF9800' },
    { name: 'Newspaper', icon: '📰', id: 'newspaper', color: '#795548' },
    { name: 'Cardboard', icon: '📦', id: 'cardboard', color: '#8BC34A' },
    { name: 'Battery', icon: '🔋', id: 'battery', color: '#F44336' },
  ];

  const handleQuickItemSelect = (itemId) => {
    const item = quickItems.find(item => item.id === itemId);
    let material = 'mixed';
    let category = 'General Waste';
    let disposal = 'Check local guidelines';
    let tips = ['Identify material first', 'Clean if necessary'];
    let binColor = 'gray';

    switch(itemId) {
      case 'plastic-bottle':
        material = 'plastic';
        category = 'Recyclable';
        disposal = 'Recycle in blue bin';
        tips = ['Rinse thoroughly', 'Remove cap', 'Flatten if possible'];
        binColor = 'blue';
        break;
      case 'glass-jar':
        material = 'glass';
        category = 'Recyclable';
        disposal = 'Recycle in green bin';
        tips = ['Rinse clean', 'Remove metal lids', 'No broken glass'];
        binColor = 'green';
        break;
      case 'aluminum-can':
        material = 'metal';
        category = 'Recyclable';
        disposal = 'Recycle in blue bin';
        tips = ['Rinse clean', 'Crush to save space', 'Remove labels'];
        binColor = 'blue';
        break;
      case 'newspaper':
        material = 'paper';
        category = 'Recyclable';
        disposal = 'Recycle in blue bin';
        tips = ['Keep dry', 'Bundle together', 'Remove plastic wrap'];
        binColor = 'blue';
        break;
      case 'cardboard':
        material = 'paper';
        category = 'Recyclable';
        disposal = 'Recycle in blue bin';
        tips = ['Flatten boxes', 'Remove tape', 'Keep dry'];
        binColor = 'blue';
        break;
      case 'battery':
        material = 'hazardous';
        category = 'Special Waste';
        disposal = 'Special disposal required';
        tips = ['Do not put in bins', 'Take to collection point', 'Tape terminals'];
        binColor = 'red';
        break;
      default:
        break;
    }

    const mockResult = {
      id: itemId,
      name: item.name,
      material: material,
      category: category,
      disposal: disposal,
      tips: tips,
      confidence: '95%',
      binColor: binColor
    };

    analyzeItem(mockResult);
  };

  const triggerFileInput = () => {
    fileInputRef.current?.click();
  };

  // FIXED: Back to home handler
  const handleBackToHome = () => {
    console.log('Back to home clicked from Identification');
    
    // Stop camera if active
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
      setStream(null);
    }
    
    // Reset states
    setCameraActive(false);
    setImagePreview(null);
    setSelectedImage(null);
    
    // Navigate
    if (typeof onNavigate === 'function') {
      onNavigate('home');
    } else {
      console.error('onNavigate is not a function');
    }
  };

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (stream) {
        stream.getTracks().forEach(track => track.stop());
      }
    };
  }, [stream]);

  return (
    <div className="identification-page">
      <div className="container">
        {/* FIXED: Back Button */}
        <motion.button
          className="back-btn"
          onClick={handleBackToHome}
          whileHover={{ x: -5 }}
          whileTap={{ scale: 0.95 }}
        >
          ← Back to Home
        </motion.button>

        {/* Page Header */}
        <motion.div
          className="page-header"
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <h1>Waste Identification</h1>
          <p>Choose your preferred method to identify how to dispose of your item</p>
          {selectedLocale && (
            <div className="locale-badge">
              📍 {selectedLocale}
            </div>
          )}
        </motion.div>

        {/* FIXED: Loading Overlay */}
        <AnimatePresence>
          {isAnalyzing && (
            <motion.div
              className="analyzing-overlay"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
            >
              <div className="analyzing-content">
                <div className="spinner"></div>
                <h3>Analyzing...</h3>
                <p>Our AI is identifying the best disposal method</p>
              </div>
            </motion.div>
          )}
        </AnimatePresence>

        {/* Analysis Options */}
        <div className="analysis-options">
          {/* Tab Selection */}
          <div className="tabs">
            <motion.button
              className={`tab ${activeTab === 'camera' ? 'active' : ''}`}
              onClick={() => setActiveTab('camera')}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
            >
              📷 Camera
            </motion.button>
            <motion.button
              className={`tab ${activeTab === 'upload' ? 'active' : ''}`}
              onClick={() => setActiveTab('upload')}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
            >
              🖼️ Upload
            </motion.button>
            <motion.button
              className={`tab ${activeTab === 'search' ? 'active' : ''}`}
              onClick={() => setActiveTab('search')}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
            >
              🔍 Search
            </motion.button>
            <motion.button
              className={`tab ${activeTab === 'quick' ? 'active' : ''}`}
              onClick={() => setActiveTab('quick')}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
            >
              ⚡ Quick Select
            </motion.button>
          </div>

          {/* Tab Content */}
          <div className="tab-content">
            {/* FIXED: Camera Tab */}
            {activeTab === 'camera' && (
              <motion.div
                className="camera-section"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ duration: 0.3 }}
              >
                <p className="section-description">
                  Take a photo using your device camera
                </p>
                
                {cameraError && (
                  <div className="camera-error">
                    ⚠️ {cameraError}
                  </div>
                )}
                
                <div className="camera-container">
                  {!cameraActive && !imagePreview && (
                    <motion.button
                      className="btn btn-primary btn-large"
                      onClick={startCamera}
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                    >
                      📸 Start Camera
                    </motion.button>
                  )}

                  {cameraActive && (
                    <div className="video-wrapper">
                      <video
                        ref={videoRef}
                        autoPlay
                        playsInline
                        muted
                        className="camera-video"
                        style={{
                          width: '100%',
                          maxWidth: '640px',
                          height: 'auto',
                          borderRadius: '15px',
                          backgroundColor: '#000'
                        }}
                      />
                      <div className="camera-controls">
                        <motion.button
                          className="btn btn-primary"
                          onClick={capturePhoto}
                          whileHover={{ scale: 1.05 }}
                          whileTap={{ scale: 0.95 }}
                        >
                          📸 Capture Photo
                        </motion.button>
                        <motion.button
                          className="btn btn-secondary"
                          onClick={stopCamera}
                          whileHover={{ scale: 1.05 }}
                          whileTap={{ scale: 0.95 }}
                        >
                          ❌ Cancel
                        </motion.button>
                      </div>
                    </div>
                  )}

                  {imagePreview && !cameraActive && (
                    <div className="preview-container">
                      <img src={imagePreview} alt="Captured" className="preview-image" />
                      <div className="preview-actions">
                        <motion.button
                          className="btn btn-primary"
                          onClick={handleImageAnalysis}
                          whileHover={{ scale: 1.05 }}
                          whileTap={{ scale: 0.95 }}
                        >
                          🔍 Analyze Image
                        </motion.button>
                        <motion.button
                          className="btn btn-secondary"
                          onClick={() => {
                            setImagePreview(null);
                            setSelectedImage(null);
                          }}
                          whileHover={{ scale: 1.05 }}
                          whileTap={{ scale: 0.95 }}
                        >
                          🔄 Retake
                        </motion.button>
                      </div>
                    </div>
                  )}
                  
                  <canvas ref={canvasRef} style={{ display: 'none' }} />
                </div>
              </motion.div>
            )}

            {/* Upload Tab */}
            {activeTab === 'upload' && (
              <motion.div
                className="upload-section"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ duration: 0.3 }}
              >
                <p className="section-description">
                  Choose an image from your device
                </p>
                
                <input
                  type="file"
                  ref={fileInputRef}
                  onChange={handleImageUpload}
                  accept="image/*"
                  style={{ display: 'none' }}
                />

                {!imagePreview && (
                  <motion.div
                    className="upload-area"
                    onClick={triggerFileInput}
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                  >
                    <div className="upload-icon">📁</div>
                    <p>Click to select an image</p>
                    <span>or drag and drop here</span>
                  </motion.div>
                )}

                {imagePreview && (
                  <div className="preview-container">
                    <img src={imagePreview} alt="Uploaded" className="preview-image" />
                    <div className="preview-actions">
                      <motion.button
                        className="btn btn-primary"
                        onClick={handleImageAnalysis}
                        whileHover={{ scale: 1.05 }}
                        whileTap={{ scale: 0.95 }}
                      >
                        🔍 Analyze Image
                      </motion.button>
                      <motion.button
                        className="btn btn-secondary"
                        onClick={() => {
                          setImagePreview(null);
                          setSelectedImage(null);
                        }}
                        whileHover={{ scale: 1.05 }}
                        whileTap={{ scale: 0.95 }}
                      >
                        🔄 Choose Another
                      </motion.button>
                    </div>
                  </div>
                )}
              </motion.div>
            )}

            {/* Search Tab */}
            {activeTab === 'search' && (
              <motion.div
                className="search-section"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ duration: 0.3 }}
              >
                <p className="section-description">
                  Type the name of the item you want to dispose
                </p>
                
                <form onSubmit={handleTextSearch} className="search-form">
                  <input
                    type="text"
                    placeholder="e.g., Plastic bottle, Glass jar, Battery..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="search-input"
                  />
                  <motion.button
                    type="submit"
                    className="btn btn-primary"
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                  >
                    🔍 Search
                  </motion.button>
                </form>
              </motion.div>
            )}

            {/* Quick Select Tab */}
            {activeTab === 'quick' && (
              <motion.div
                className="quick-section"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ duration: 0.3 }}
              >
                <p className="section-description">
                  Select an item to get instant disposal guidance
                </p>
                
                <div className="quick-items-grid">
                  {quickItems.map((item, index) => (
                    <motion.div
                      key={item.id}
                      className="quick-item-card"
                      onClick={() => handleQuickItemSelect(item.id)}
                      initial={{ opacity: 0, y: 20 }}
                      animate={{ opacity: 1, y: 0 }}
                      transition={{ delay: index * 0.1 }}
                      whileHover={{ scale: 1.05, y: -5 }}
                      whileTap={{ scale: 0.95 }}
                      style={{ '--item-color': item.color }}
                    >
                      <div className="quick-item-icon">{item.icon}</div>
                      <div className="quick-item-name">{item.name}</div>
                    </motion.div>
                  ))}
                </div>
              </motion.div>
            )}
          </div>
        </div>

        {/* Tips Section */}
        <motion.div
          className="tips-section"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.5 }}
        >
          <h3>💡 Tips for Better Results</h3>
          <div className="tips-grid">
            <motion.div
              className="tip-card"
              whileHover={{ y: -5 }}
            >
              <span className="tip-icon">📸</span>
              <p>Take photos in good lighting with a clear view of the item</p>
            </motion.div>
            <motion.div
              className="tip-card"
              whileHover={{ y: -5 }}
            >
              <span className="tip-icon">🧼</span>
              <p>Make sure items are clean and dry for accurate recycling</p>
            </motion.div>
            <motion.div
              className="tip-card"
              whileHover={{ y: -5 }}
            >
              <span className="tip-icon">🔍</span>
              <p>Look for recycling symbols and material information</p>
            </motion.div>
            <motion.div
              className="tip-card"
              whileHover={{ y: -5 }}
            >
              <span className="tip-icon">📝</span>
              <p>Use detailed descriptions when searching for items</p>
            </motion.div>
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default Identification;
