import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { fadeInUp, staggerContainer } from '../../utils/animations';
import './Identification.css';

const Identification = ({ onNavigate, selectedLocale, onResult }) => {
  const [activeTab, setActiveTab] = useState('camera');
  const [searchQuery, setSearchQuery] = useState('');
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [cameraActive, setCameraActive] = useState(false);
  const [stream, setStream] = useState(null);
  
  const fileInputRef = useRef(null);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  // Camera functions with proper video element handling
  const startCamera = async () => {
    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({
        video: {
          facingMode: 'environment',
          width: { ideal: 1280 },
          height: { ideal: 720 }
        },
        audio: false
      });
      
      setStream(mediaStream);
      setCameraActive(true);
      
      // Wait for video element to be ready
      setTimeout(() => {
        if (videoRef.current) {
          videoRef.current.srcObject = mediaStream;
          videoRef.current.play().catch(err => {
            console.error('Error playing video:', err);
          });
        }
      }, 100);
    } catch (err) {
      console.error('Error accessing camera:', err);
      alert('Unable to access camera. Please check permissions and try again.');
    }
  };

  const stopCamera = () => {
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
      setStream(null);
    }
    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }
    setCameraActive(false);
  };

  const capturePhoto = () => {
    if (videoRef.current && canvasRef.current) {
      const context = canvasRef.current.getContext('2d');
      canvasRef.current.width = videoRef.current.videoWidth;
      canvasRef.current.height = videoRef.current.videoHeight;
      context.drawImage(videoRef.current, 0, 0);
      
      const dataUrl = canvasRef.current.toDataURL('image/jpeg');
      setImagePreview(dataUrl);
      setSelectedImage(dataUrl);
      stopCamera();
    }
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedImage(file);
      const reader = new FileReader();
      reader.onload = (e) => {
        setImagePreview(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleImageAnalysis = () => {
    if (!selectedImage) return;
    
    setIsAnalyzing(true);
    
    // Simulate AI analysis
    setTimeout(() => {
      setIsAnalyzing(false);
      const mockResult = {
        name: 'Plastic Bottle',
        material: 'Plastic',
        category: 'Recyclable',
        disposal: 'Recycle in blue bin',
        tips: [
          'Rinse before recycling',
          'Remove caps if separate material',
          'Check local recycling guidelines'
        ],
        confidence: '92%',
        binColor: 'blue'
      };
      
      // Navigate to results page with data
      if (onNavigate) {
        onNavigate('results', mockResult);
      }
    }, 2000);
  };

  const handleTextSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      setIsAnalyzing(true);
      
      setTimeout(() => {
        setIsAnalyzing(false);
        const query = searchQuery.toLowerCase();
        let material = 'Mixed';
        let category = 'General Waste';
        let disposal = 'Check local guidelines';
        let tips = ['Clean before disposal', 'Separate different materials'];
        let binColor = 'gray';

        if (query.includes('plastic') || query.includes('bottle')) {
          material = 'Plastic';
          category = 'Recyclable';
          disposal = 'Recycle in blue bin';
          tips = ['Rinse thoroughly', 'Remove caps', 'Flatten if possible'];
          binColor = 'blue';
        } else if (query.includes('glass') || query.includes('jar')) {
          material = 'Glass';
          category = 'Recyclable';
          disposal = 'Recycle in green bin';
          tips = ['Rinse clean', 'Remove lids', 'No broken glass in recycling'];
          binColor = 'green';
        } else if (query.includes('metal') || query.includes('can')) {
          material = 'Metal';
          category = 'Recyclable';
          disposal = 'Recycle in blue bin';
          tips = ['Rinse clean', 'Remove labels if easy', 'Crush to save space'];
          binColor = 'blue';
        } else if (query.includes('paper') || query.includes('cardboard')) {
          material = 'Paper';
          category = 'Recyclable';
          disposal = 'Recycle in blue bin';
          tips = ['Keep dry and clean', 'Flatten boxes', 'Remove plastic wrapping'];
          binColor = 'blue';
        } else if (query.includes('food') || query.includes('organic')) {
          material = 'Organic';
          category = 'Compostable';
          disposal = 'Compost in green bin';
          tips = ['No plastic contamination', 'Use compostable bags', 'Keep lid closed'];
          binColor = 'green';
        } else if (query.includes('battery')) {
          material = 'Hazardous';
          category = 'Special Waste';
          disposal = 'Special disposal required';
          tips = ['Do not put in regular bins', 'Take to collection point', 'Tape terminals if lithium'];
          binColor = 'red';
        }

        const mockResult = {
          name: searchQuery,
          material: material,
          category: category,
          disposal: disposal,
          tips: tips,
          confidence: '90%',
          binColor: binColor
        };

        // Navigate to results page with data
        if (onNavigate) {
          onNavigate('results', mockResult);
        }
      }, 1500);
    }
  };

  const quickItems = [
    { name: 'Plastic Bottle', icon: 'üß¥', id: 'plastic-bottle', color: '#2196F3' },
    { name: 'Glass Jar', icon: 'üç∂', id: 'glass-jar', color: '#4CAF50' },
    { name: 'Aluminum Can', icon: 'ü•´', id: 'aluminum-can', color: '#FF9800' },
    { name: 'Newspaper', icon: 'üì∞', id: 'newspaper', color: '#795548' },
    { name: 'Cardboard', icon: 'üì¶', id: 'cardboard', color: '#8BC34A' },
    { name: 'Battery', icon: 'üîã', id: 'battery', color: '#F44336' },
  ];

  const handleQuickItemSelect = (itemId) => {
    setIsAnalyzing(true);
    
    setTimeout(() => {
      const item = quickItems.find(item => item.id === itemId);
      let material = 'Mixed';
      let category = 'General Waste';
      let disposal = 'Check local guidelines';
      let tips = ['Identify material first', 'Clean if necessary'];
      let binColor = 'gray';

      switch(itemId) {
        case 'plastic-bottle':
          material = 'Plastic';
          category = 'Recyclable';
          disposal = 'Recycle in blue bin';
          tips = ['Rinse thoroughly', 'Remove cap', 'Flatten if possible'];
          binColor = 'blue';
          break;
        case 'glass-jar':
          material = 'Glass';
          category = 'Recyclable';
          disposal = 'Recycle in green bin';
          tips = ['Rinse clean', 'Remove metal lids', 'No broken glass'];
          binColor = 'green';
          break;
        case 'aluminum-can':
          material = 'Metal';
          category = 'Recyclable';
          disposal = 'Recycle in blue bin';
          tips = ['Rinse clean', 'Crush to save space', 'Remove paper labels'];
          binColor = 'blue';
          break;
        case 'newspaper':
          material = 'Paper';
          category = 'Recyclable';
          disposal = 'Recycle in blue bin';
          tips = ['Keep dry', 'Bundle together', 'Remove plastic wrap'];
          binColor = 'blue';
          break;
        case 'cardboard':
          material = 'Paper';
          category = 'Recyclable';
          disposal = 'Recycle in blue bin';
          tips = ['Flatten boxes', 'Remove tape', 'Keep dry'];
          binColor = 'blue';
          break;
        case 'battery':
          material = 'Hazardous';
          category = 'Special Waste';
          disposal = 'Special disposal required';
          tips = ['Do not put in bins', 'Take to collection point', 'Tape terminals'];
          binColor = 'red';
          break;
        default:
          break;
      }

      const mockResult = {
        name: item.name,
        material: material,
        category: category,
        disposal: disposal,
        tips: tips,
        confidence: '95%',
        binColor: binColor
      };

      setIsAnalyzing(false);
      
      // Navigate to results page with data
      if (onNavigate) {
        onNavigate('results', mockResult);
      }
    }, 1000);
  };

  const triggerFileInput = () => {
    fileInputRef.current?.click();
  };

  // Cleanup camera on unmount
  useEffect(() => {
    return () => {
      if (stream) {
        stream.getTracks().forEach(track => track.stop());
      }
    };
  }, [stream]);

  return (
    <div className="identification-page">
      <div className="container">
        {/* FIXED: Back to Home Button */}
        <motion.button
          className="back-btn"
          onClick={() => onNavigate('home')}
          whileHover={{ x: -5 }}
          whileTap={{ scale: 0.95 }}
        >
          ‚Üê Back to Home
        </motion.button>

        {/* Page Header */}
        <motion.div
          className="page-header"
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <h1>Waste Identification</h1>
          <p>Choose your preferred method to identify how to dispose of your item</p>
          {selectedLocale && (
            <div className="locale-badge">
              üìç {selectedLocale}
            </div>
          )}
        </motion.div>

        {/* Loading Overlay */}
        <AnimatePresence>
          {isAnalyzing && (
            <motion.div
              className="analyzing-overlay"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
            >
              <div className="analyzing-content">
                <div className="spinner"></div>
                <h3>Analyzing...</h3>
                <p>Our AI is identifying the best disposal method</p>
              </div>
            </motion.div>
          )}
        </AnimatePresence>

        {/* Analysis Options */}
        <div className="analysis-options">
          {/* Tab Selection */}
          <div className="tabs">
            <button
              className={`tab ${activeTab === 'camera' ? 'active' : ''}`}
              onClick={() => setActiveTab('camera')}
            >
              üì∑ Camera
            </button>
            <button
              className={`tab ${activeTab === 'upload' ? 'active' : ''}`}
              onClick={() => setActiveTab('upload')}
            >
              üñºÔ∏è Upload
            </button>
            <button
              className={`tab ${activeTab === 'search' ? 'active' : ''}`}
              onClick={() => setActiveTab('search')}
            >
              üîç Search
            </button>
            <button
              className={`tab ${activeTab === 'quick' ? 'active' : ''}`}
              onClick={() => setActiveTab('quick')}
            >
              ‚ö° Quick Select
            </button>
          </div>

          {/* Tab Content */}
          <div className="tab-content">
            {/* Camera Tab */}
            {activeTab === 'camera' && (
              <motion.div
                className="camera-section"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ duration: 0.3 }}
              >
                <p className="section-description">
                  Take a photo using your device camera
                </p>
                
                <div className="camera-container">
                  {!cameraActive && !imagePreview && (
                    <motion.button
                      className="btn btn-primary btn-large"
                      onClick={startCamera}
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                    >
                      üì∏ Start Camera
                    </motion.button>
                  )}

                  {cameraActive && (
                    <div className="video-wrapper">
                      <video
                        ref={videoRef}
                        autoPlay
                        playsInline
                        muted
                        className="camera-video"
                      />
                      <div className="camera-controls">
                        <button
                          className="btn btn-primary"
                          onClick={capturePhoto}
                        >
                          üì∏ Capture
                        </button>
                        <button
                          className="btn btn-secondary"
                          onClick={stopCamera}
                        >
                          ‚ùå Cancel
                        </button>
                      </div>
                    </div>
                  )}

                  {imagePreview && (
                    <div className="preview-container">
                      <img src={imagePreview} alt="Captured" className="preview-image" />
                      <div className="preview-actions">
                        <button
                          className="btn btn-primary"
                          onClick={handleImageAnalysis}
                        >
                          üîç Analyze Image
                        </button>
                        <button
                          className="btn btn-secondary"
                          onClick={() => {
                            setImagePreview(null);
                            setSelectedImage(null);
                          }}
                        >
                          üîÑ Retake
                        </button>
                      </div>
                    </div>
                  )}
                  
                  <canvas ref={canvasRef} style={{ display: 'none' }} />
                </div>
              </motion.div>
            )}

            {/* Upload Tab */}
            {activeTab === 'upload' && (
              <motion.div
                className="upload-section"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ duration: 0.3 }}
              >
                <p className="section-description">
                  Choose an image from your device
                </p>
                
                <input
                  type="file"
                  ref={fileInputRef}
                  onChange={handleImageUpload}
                  accept="image/*"
                  style={{ display: 'none' }}
                />

                {!imagePreview && (
                  <div className="upload-area" onClick={triggerFileInput}>
                    <div className="upload-icon">üìÅ</div>
                    <p>Click to select an image</p>
                    <span>or drag and drop here</span>
                  </div>
                )}

                {imagePreview && (
                  <div className="preview-container">
                    <img src={imagePreview} alt="Uploaded" className="preview-image" />
                    <div className="preview-actions">
                      <button
                        className="btn btn-primary"
                        onClick={handleImageAnalysis}
                      >
                        üîç Analyze Image
                      </button>
                      <button
                        className="btn btn-secondary"
                        onClick={() => {
                          setImagePreview(null);
                          setSelectedImage(null);
                        }}
                      >
                        üîÑ Choose Another
                      </button>
                    </div>
                  </div>
                )}
              </motion.div>
            )}

            {/* Search Tab */}
            {activeTab === 'search' && (
              <motion.div
                className="search-section"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ duration: 0.3 }}
              >
                <p className="section-description">
                  Type the name of the item you want to dispose
                </p>
                
                <form onSubmit={handleTextSearch} className="search-form">
                  <input
                    type="text"
                    placeholder="e.g., Plastic bottle, Glass jar, Battery..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="search-input"
                  />
                  <button type="submit" className="btn btn-primary">
                    üîç Search
                  </button>
                </form>
              </motion.div>
            )}

            {/* Quick Select Tab */}
            {activeTab === 'quick' && (
              <motion.div
                className="quick-section"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ duration: 0.3 }}
              >
                <p className="section-description">
                  Select an item to get instant disposal guidance
                </p>
                
                <div className="quick-items-grid">
                  {quickItems.map((item, index) => (
                    <motion.div
                      key={item.id}
                      className="quick-item-card"
                      onClick={() => handleQuickItemSelect(item.id)}
                      initial={{ opacity: 0, y: 20 }}
                      animate={{ opacity: 1, y: 0 }}
                      transition={{ delay: index * 0.1 }}
                      whileHover={{ scale: 1.05, y: -5 }}
                      whileTap={{ scale: 0.95 }}
                      style={{ borderColor: item.color }}
                    >
                      <div className="quick-item-icon">{item.icon}</div>
                      <div className="quick-item-name">{item.name}</div>
                    </motion.div>
                  ))}
                </div>
              </motion.div>
            )}
          </div>
        </div>

        {/* Tips Section */}
        <motion.div
          className="tips-section"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.5 }}
        >
          <h3>üí° Tips for Better Results</h3>
          <div className="tips-grid">
            <div className="tip-card">
              <span className="tip-icon">üì∏</span>
              <p>Take photos in good lighting with a clear view of the item</p>
            </div>
            <div className="tip-card">
              <span className="tip-icon">üßº</span>
              <p>Make sure items are clean and dry for accurate recycling</p>
            </div>
            <div className="tip-card">
              <span className="tip-icon">üîç</span>
              <p>Look for recycling symbols and material information</p>
            </div>
            <div className="tip-card">
              <span className="tip-icon">üìù</span>
              <p>Use detailed descriptions when searching for items</p>
            </div>
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default Identification;
