// Identification.jsx - Fixed Version
import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { fadeInUp, staggerContainer } from '../../utils/animations';
import './Identification.css';

const Identification = ({ onNavigate, selectedLocale, onResult }) => {
  const [activeTab, setActiveTab] = useState('camera');
  const [searchQuery, setSearchQuery] = useState('');
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [cameraActive, setCameraActive] = useState(false);
  const [stream, setStream] = useState(null);
  const fileInputRef = useRef(null);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  // Camera functions
  const startCamera = async () => {
    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({ 
        video: { 
          facingMode: 'environment',
          width: { ideal: 1280 },
          height: { ideal: 720 }
        }, 
        audio: false 
      });
      setStream(mediaStream);
      if (videoRef.current) {
        videoRef.current.srcObject = mediaStream;
      }
      setCameraActive(true);
    } catch (err) {
      console.error('Error accessing camera:', err);
      alert('Unable to access camera. Please check permissions and try again.');
    }
  };

  const stopCamera = () => {
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
      setStream(null);
    }
    setCameraActive(false);
  };

  const capturePhoto = () => {
    if (videoRef.current && canvasRef.current) {
      const context = canvasRef.current.getContext('2d');
      canvasRef.current.width = videoRef.current.videoWidth;
      canvasRef.current.height = videoRef.current.videoHeight;
      context.drawImage(videoRef.current, 0, 0);
      
      const dataUrl = canvasRef.current.toDataURL('image/jpeg');
      setImagePreview(dataUrl);
      setSelectedImage(dataUrl);
      stopCamera();
    }
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedImage(file);
      const reader = new FileReader();
      reader.onload = (e) => {
        setImagePreview(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleImageAnalysis = () => {
    if (!selectedImage) return;
    
    setIsAnalyzing(true);
    // Simulate AI analysis with better mock data
    setTimeout(() => {
      setIsAnalyzing(false);
      const mockResult = {
        id: 'analyzed-item',
        name: 'Plastic Water Bottle',
        material: 'plastic',
        category: 'Recyclable',
        disposal: 'Recycle in blue bin after rinsing',
        tips: [
          'Rinse thoroughly before recycling',
          'Remove caps and labels if separate',
          'Flatten to save space',
          'Check local guidelines for specific rules'
        ],
        confidence: '92%',
        binColor: 'blue',
        environmentalImpact: 'Saves energy and reduces landfill waste'
      };
      if (onResult) onResult(mockResult);
    }, 2500);
  };

  const handleTextSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      setIsAnalyzing(true);
      // Enhanced mock result based on search query
      setTimeout(() => {
        setIsAnalyzing(false);
        const query = searchQuery.toLowerCase();
        let resultData = {
          name: searchQuery,
          material: 'mixed',
          category: 'General Waste',
          disposal: 'Check local guidelines for proper disposal',
          tips: ['Clean before disposal', 'Separate different materials if possible'],
          confidence: '85%',
          binColor: 'gray',
          environmentalImpact: 'Proper disposal helps reduce environmental impact'
        };

        if (query.includes('plastic') || query.includes('bottle')) {
          resultData = {
            name: 'Plastic Bottle',
            material: 'plastic',
            category: 'Recyclable',
            disposal: 'Recycle in blue bin after rinsing',
            tips: [
              'Rinse thoroughly before recycling',
              'Remove caps and labels',
              'Flatten to save space',
              'Check local recycling guidelines'
            ],
            confidence: '94%',
            binColor: 'blue',
            environmentalImpact: 'Recycling saves energy and reduces plastic pollution'
          };
        } else if (query.includes('glass') || query.includes('jar')) {
          resultData = {
            name: 'Glass Container',
            material: 'glass',
            category: 'Recyclable',
            disposal: 'Recycle in green bin',
            tips: [
              'Rinse clean before recycling',
              'Remove metal lids separately',
              'No broken glass in recycling',
              'Check color separation requirements'
            ],
            confidence: '96%',
            binColor: 'green',
            environmentalImpact: 'Glass is 100% recyclable and can be reused endlessly'
          };
        } else if (query.includes('battery')) {
          resultData = {
            name: 'Battery',
            material: 'hazardous',
            category: 'Special Waste',
            disposal: 'Special disposal required - take to collection point',
            tips: [
              'Do not put in regular bins',
              'Take to battery collection point',
              'Tape terminals if lithium battery',
              'Store in cool dry place until disposal'
            ],
            confidence: '98%',
            binColor: 'red',
            environmentalImpact: 'Proper disposal prevents soil and water contamination'
          };
        }

        if (onResult) onResult(resultData);
      }, 1800);
    }
  };

  const quickItems = [
    { name: 'Plastic Bottle', icon: 'üß¥', id: 'plastic-bottle', color: '#2196F3' },
    { name: 'Glass Jar', icon: 'üç∂', id: 'glass-jar', color: '#4CAF50' },
    { name: 'Aluminum Can', icon: 'ü•´', id: 'aluminum-can', color: '#FF9800' },
    { name: 'Newspaper', icon: 'üì∞', id: 'newspaper', color: '#795548' },
    { name: 'Cardboard', icon: 'üì¶', id: 'cardboard', color: '#8BC34A' },
    { name: 'Battery', icon: 'üîã', id: 'battery', color: '#F44336' },
  ];

  const handleQuickItemSelect = (itemId) => {
    setIsAnalyzing(true);
    setTimeout(() => {
      const item = quickItems.find(item => item.id === itemId);
      let resultData = {
        name: item.name,
        material: 'mixed',
        category: 'General Waste',
        disposal: 'Check local guidelines',
        tips: ['Identify material first', 'Clean if necessary'],
        confidence: '90%',
        binColor: 'gray',
        environmentalImpact: 'Proper sorting helps recycling efficiency'
      };

      switch(itemId) {
        case 'plastic-bottle':
          resultData = {
            name: 'Plastic Bottle',
            material: 'plastic',
            category: 'Recyclable',
            disposal: 'Recycle in blue bin',
            tips: [
              'Rinse thoroughly before recycling',
              'Remove cap and recycle separately',
              'Flatten to save space',
              'Check resin code for specific type'
            ],
            confidence: '95%',
            binColor: 'blue',
            environmentalImpact: 'Recycling one plastic bottle saves enough energy to power a 60W bulb for 6 hours'
          };
          break;
        case 'glass-jar':
          resultData = {
            name: 'Glass Jar',
            material: 'glass',
            category: 'Recyclable',
            disposal: 'Recycle in green bin',
            tips: [
              'Rinse clean before recycling',
              'Remove metal lids separately',
              'No broken glass in recycling',
              'Labels can usually stay on'
            ],
            confidence: '97%',
            binColor: 'green',
            environmentalImpact: 'Glass recycling saves 30% energy compared to new production'
          };
          break;
        case 'aluminum-can':
          resultData = {
            name: 'Aluminum Can',
            material: 'metal',
            category: 'Recyclable',
            disposal: 'Recycle in blue bin',
            tips: [
              'Rinse clean before recycling',
              'Crush to save space',
              'Remove paper labels if easy',
              'Keep dry to prevent contamination'
            ],
            confidence: '96%',
            binColor: 'blue',
            environmentalImpact: 'Recycling aluminum saves 95% energy compared to new production'
          };
          break;
        case 'battery':
          resultData = {
            name: 'Battery',
            material: 'hazardous',
            category: 'Special Waste',
            disposal: 'Special disposal required',
            tips: [
              'Do not put in regular bins',
              'Take to battery collection point',
              'Tape terminals if lithium battery',
              'Store properly until disposal'
            ],
            confidence: '98%',
            binColor: 'red',
            environmentalImpact: 'Proper disposal prevents heavy metal contamination in soil and water'
          };
          break;
        default:
          break;
      }

      setIsAnalyzing(false);
      if (onResult) onResult(resultData);
    }, 1200);
  };

  const triggerFileInput = () => {
    fileInputRef.current?.click();
  };

  // Cleanup camera on unmount
  useEffect(() => {
    return () => {
      if (stream) {
        stream.getTracks().forEach(track => track.stop());
      }
    };
  }, [stream]);

  // Fix for back button navigation
  const handleBackToHome = () => {
    if (typeof onNavigate === 'function') {
      onNavigate('home');
    }
  };

  return (
    <div className="identification-page">
      <div className="container">
        {/* Fixed Back Button */}
        <motion.button 
          className="back-btn"
          onClick={handleBackToHome}
          initial={{ opacity: 0, x: -20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.5 }}
          whileHover={{ x: -5, scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          ‚Üê Back to Home
        </motion.button>

        <motion.div
          className="page-header"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <h1>Identify Your Item</h1>
          <p>Choose your preferred method to identify how to dispose of your item</p>
          <div className="locale-badge">
            üìç Currently viewing rules for: <strong>{selectedLocale}</strong>
          </div>
        </motion.div>

        {/* Analysis Overlay */}
        <AnimatePresence>
          {isAnalyzing && (
            <motion.div 
              className="analysis-overlay"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
            >
              <motion.div 
                className="analysis-content"
                initial={{ scale: 0.8, opacity: 0 }}
                animate={{ scale: 1, opacity: 1 }}
                transition={{ type: "spring", stiffness: 300, damping: 20 }}
              >
                <motion.div 
                  className="loading-spinner"
                  animate={{ rotate: 360 }}
                  transition={{ duration: 2, repeat: Infinity, ease: "linear" }}
                >
                  ‚ôªÔ∏è
                </motion.div>
                <h3>Analyzing your item...</h3>
                <p>Our AI is identifying the best disposal method</p>
                <motion.div 
                  className="loading-dots"
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ delay: 0.5 }}
                >
                  <span></span>
                  <span></span>
                  <span></span>
                </motion.div>
                <motion.p
                  className="analysis-tip"
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ delay: 1 }}
                >
                  Tip: Make sure items are clean for better recycling!
                </motion.p>
              </motion.div>
            </motion.div>
          )}
        </AnimatePresence>

        {/* Rest of the component remains the same but with enhanced animations */}
        {/* ... (previous tab content structure) */}
      </div>
    </div>
  );
};

export default Identification;
