import React, { useEffect, useRef } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';
import { fadeInUp, staggerContainer } from '../../utils/animations';
import './Home.css';

const Home = ({ onNavigate }) => {
  const featuresRef = useRef([]);
  const heroRef = useRef(null);
  
  const { scrollYProgress } = useScroll({
    target: heroRef,
    offset: ["start start", "end start"]
  });

  const y = useTransform(scrollYProgress, [0, 1], ['0%', '50%']);
  const opacity = useTransform(scrollYProgress, [0, 1], [1, 0]);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            entry.target.classList.add('visible');
          }
        });
      },
      { threshold: 0.1 }
    );

    featuresRef.current.forEach(el => {
      if (el) observer.observe(el);
    });

    return () => observer.disconnect();
  }, []);

  const features = [
    {
      icon: '📷',
      title: 'AI-Powered Identification',
      description: 'Simply take a photo or upload an image to identify waste items instantly.'
    },
    {
      icon: '🗑️',
      title: 'Smart Sorting',
      description: 'Get accurate disposal instructions based on your local recycling rules.'
    },
    {
      icon: '♻️',
      title: 'Upcycling Ideas',
      description: 'Discover creative ways to reuse items instead of throwing them away.'
    },
    {
      icon: '🌍',
      title: 'Localized Guidance',
      description: "Tailored advice for your city's specific waste management system."
    }
  ];

  const stats = [
    { number: '90%', label: 'Recycling Accuracy' },
    { number: '15+', label: 'Cities Supported' },
    { number: '50+', label: 'Items Identifiable' },
    { number: '24/7', label: 'Available' }
  ];

  const howItWorks = [
    {
      step: '1',
      title: 'Capture',
      description: 'Take or upload a photo of your waste item',
      icon: '📸'
    },
    {
      step: '2',
      title: 'Identify',
      description: 'AI analyzes and identifies the material',
      icon: '🔍'
    },
    {
      step: '3',
      title: 'Sort',
      description: 'Get sorting instructions for your area',
      icon: '♻️'
    },
    {
      step: '4',
      title: 'Impact',
      description: 'Track your environmental contribution',
      icon: '🌱'
    }
  ];

  const handleNavigate = (page) => {
    if (typeof onNavigate === 'function') {
      onNavigate(page);
    } else {
      console.error("onNavigate function not provided to Home component");
    }
  };

  return (
    <div className="home-page">
      {/* Hero Section */}
      <motion.section 
        ref={heroRef}
        className="hero"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1 }}
      >
        <motion.div 
          className="hero-content"
          style={{ y, opacity }}
        >
          <motion.h1
            initial={{ y: 50, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ delay: 0.2, duration: 0.8 }}
          >
            Smart Waste Disposal
            <br />
            <span className="gradient-text">Made Simple</span>
          </motion.h1>
          
          <motion.p
            className="hero-description"
            initial={{ y: 30, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ delay: 0.4, duration: 0.8 }}
          >
            Identify, sort, and dispose of waste correctly with AI-powered guidance
          </motion.p>

          <motion.div
            className="hero-buttons"
            initial={{ y: 30, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ delay: 0.6, duration: 0.8 }}
          >
            <motion.button
              className="btn btn-primary"
              onClick={() => handleNavigate('scanner')}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              Start Scanning
            </motion.button>
            <motion.button
              className="btn btn-secondary"
              onClick={() => handleNavigate('upcycling')}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              Explore Upcycling
            </motion.button>
          </motion.div>
        </motion.div>

        <div className="hero-illustration">
          <motion.div
            className="floating-element"
            animate={{
              y: [0, -20, 0],
              rotate: [0, 5, 0]
            }}
            transition={{
              duration: 4,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          >
            🌍
          </motion.div>
        </div>
      </motion.section>

      {/* Stats Section */}
      <section className="stats-section">
        <div className="stats-grid">
          {stats.map((stat, index) => (
            <motion.div
              key={index}
              className="stat-card"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ delay: index * 0.1 }}
            >
              <div className="stat-number">{stat.number}</div>
              <div className="stat-label">{stat.label}</div>
            </motion.div>
          ))}
        </div>
      </section>

      {/* Features Section */}
      <section className="features-section">
        <motion.h2
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
        >
          Powerful features to make recycling effortless and effective
        </motion.h2>
        
        <div className="features-grid">
          {features.map((feature, index) => (
            <motion.div
              key={index}
              ref={el => featuresRef.current[index] = el}
              className="feature-card"
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ delay: index * 0.1 }}
              whileHover={{ 
                y: -10,
                boxShadow: '0 20px 40px rgba(0,0,0,0.1)'
              }}
            >
              <div className="feature-icon">{feature.icon}</div>
              <h3>{feature.title}</h3>
              <p>{feature.description}</p>
            </motion.div>
          ))}
        </div>
      </section>

      {/* How It Works Section */}
      <section className="how-it-works">
        <motion.h2
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
        >
          Four simple steps to smarter waste management
        </motion.h2>

        <div className="steps-container">
          {howItWorks.map((step, index) => (
            <motion.div
              key={index}
              className="step-card"
              initial={{ opacity: 0, x: index % 2 === 0 ? -50 : 50 }}
              whileInView={{ opacity: 1, x: 0 }}
              viewport={{ once: true }}
              transition={{ delay: index * 0.2 }}
            >
              <div className="step-number">{step.step}</div>
              <div className="step-icon">{step.icon}</div>
              <h3>{step.title}</h3>
              <p>{step.description}</p>
            </motion.div>
          ))}
        </div>
      </section>

      {/* CTA Section */}
      <motion.section
        className="cta-section"
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        viewport={{ once: true }}
      >
        <h2>Start your sustainable journey today with Smart Ecodispose</h2>
        <motion.button
          className="btn btn-primary btn-large"
          onClick={() => handleNavigate('scanner')}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          Get Started Now
        </motion.button>
      </motion.section>
    </div>
  );
};

export default Home;
