import React, { useState, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { fadeInUp, staggerContainer } from '../../utils/animations';
import './Identification.css';

const Identification = ({ onNavigate, selectedLocale, onResult }) => {
  const [activeTab, setActiveTab] = useState('camera');
  const [searchQuery, setSearchQuery] = useState('');
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  
  // New state and refs for camera functionality
  const [isCameraOpen, setIsCameraOpen] = useState(false);
  const [cameraError, setCameraError] = useState(null);
  const [stream, setStream] = useState(null);

  const fileInputRef = useRef(null);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  // --- NEW CAMERA FUNCTIONS ---

  const handleOpenCamera = async () => {
    setCameraError(null);
    if (navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {
      try {
        const streamData = await navigator.mediaDevices.getUserMedia({
          video: { facingMode: 'environment' } // Prefer rear camera
        });
        setStream(streamData);
        setIsCameraOpen(true);
      } catch (err) {
        console.error("Camera Error:", err);
        setCameraError("Could not access the camera. Please check your browser permissions and try again.");
      }
    } else {
      setCameraError("Your browser does not support camera access.");
    }
  };
  
  const handleCloseCamera = () => {
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
    }
    setIsCameraOpen(false);
    setStream(null);
  };

  const handleCapture = () => {
    if (videoRef.current && canvasRef.current) {
      const video = videoRef.current;
      const canvas = canvasRef.current;
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;
      const context = canvas.getContext('2d');
      context.drawImage(video, 0, 0, video.videoWidth, video.videoHeight);
      
      canvas.toBlob(blob => {
        const capturedFile = new File([blob], "capture.jpg", { type: "image/jpeg" });
        setSelectedImage(capturedFile);
        setImagePreview(URL.createObjectURL(capturedFile));
      }, 'image/jpeg');

      handleCloseCamera();
    }
  };
  
  // Attach stream to video element when camera is open
  React.useEffect(() => {
    if (isCameraOpen && stream && videoRef.current) {
      videoRef.current.srcObject = stream;
    }
  }, [isCameraOpen, stream]);


  // --- EXISTING FUNCTIONS (Unchanged) ---
  
  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedImage(file);
      const reader = new FileReader();
      reader.onload = (e) => {
        setImagePreview(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleImageAnalysis = () => {
    if (!selectedImage) return;
    
    setIsAnalyzing(true);
    setTimeout(() => {
      setIsAnalyzing(false);
      const mockResult = {
        name: 'Uploaded Item',
        material: 'plastic',
        category: 'Recyclable',
        disposal: 'Recycle in blue bin',
        tips: ['Rinse before recycling', 'Remove caps if separate', 'Check local guidelines'],
        confidence: '85%',
        binColor: 'blue'
      };
      if (onResult) onResult(mockResult);
    }, 2000);
  };

  const handleTextSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      setIsAnalyzing(true);
      setTimeout(() => {
        setIsAnalyzing(false);
        const query = searchQuery.toLowerCase();
        let material = 'mixed';
        let category = 'General Waste';
        let disposal = 'Check local guidelines';
        let tips = ['Clean before disposal', 'Separate different materials'];
        let binColor = 'gray';

        if (query.includes('plastic') || query.includes('bottle')) {
          material = 'plastic'; category = 'Recyclable'; disposal = 'Recycle in blue bin'; tips = ['Rinse thoroughly', 'Remove caps', 'Flatten if possible']; binColor = 'blue';
        } else if (query.includes('glass') || query.includes('jar')) {
          material = 'glass'; category = 'Recyclable'; disposal = 'Recycle in green bin'; tips = ['Rinse clean', 'Remove lids', 'No broken glass in recycling']; binColor = 'green';
        } else if (query.includes('metal') || query.includes('can')) {
          material = 'metal'; category = 'Recyclable'; disposal = 'Recycle in blue bin'; tips = ['Rinse clean', 'Remove labels if easy', 'Crush to save space']; binColor = 'blue';
        } else if (query.includes('paper') || query.includes('cardboard')) {
          material = 'paper'; category = 'Recyclable'; disposal = 'Recycle in blue bin'; tips = ['Keep dry and clean', 'Flatten boxes', 'Remove plastic wrapping']; binColor = 'blue';
        } else if (query.includes('food') || query.includes('organic')) {
          material = 'organic'; category = 'Compostable'; disposal = 'Compost in green bin'; tips = ['No plastic contamination', 'Use compostable bags', 'Keep lid closed']; binColor = 'green';
        } else if (query.includes('battery')) {
          material = 'hazardous'; category = 'Special Waste'; disposal = 'Special disposal required'; tips = ['Do not put in regular bins', 'Take to collection point', 'Tape terminals if lithium']; binColor = 'red';
        }

        const mockResult = { name: searchQuery, material, category, disposal, tips, confidence: '90%', binColor };
        if (onResult) onResult(mockResult);
      }, 1500);
    }
  };

  const quickItems = [
    { name: 'Plastic Bottle', icon: 'ðŸ§´', id: 'plastic-bottle', color: '#2196F3' },
    { name: 'Glass Jar', icon: 'ðŸ¶', id: 'glass-jar', color: '#4CAF50' },
    { name: 'Aluminum Can', icon: 'ðŸ¥«', id: 'aluminum-can', color: '#FF9800' },
    { name: 'Newspaper', icon: 'ðŸ“°', id: 'newspaper', color: '#795548' },
    { name: 'Cardboard', icon: 'ðŸ“¦', id: 'cardboard', color: '#8BC34A' },
    { name: 'Battery', icon: 'ðŸ”‹', id: 'battery', color: '#F44336' },
  ];

  const handleQuickItemSelect = (itemId) => {
    setIsAnalyzing(true);
    setTimeout(() => {
      const item = quickItems.find(item => item.id === itemId);
      let material = 'mixed', category = 'General Waste', disposal = 'Check local guidelines', tips = ['Identify material first', 'Clean if necessary'], binColor = 'gray';

      switch(itemId) {
        case 'plastic-bottle': material = 'plastic'; category = 'Recyclable'; disposal = 'Recycle in blue bin'; tips = ['Rinse thoroughly', 'Remove cap', 'Flatten if possible']; binColor = 'blue'; break;
        case 'glass-jar': material = 'glass'; category = 'Recyclable'; disposal = 'Recycle in green bin'; tips = ['Rinse clean', 'Remove metal lids', 'No broken glass']; binColor = 'green'; break;
        case 'aluminum-can': material = 'metal'; category = 'Recyclable'; disposal = 'Recycle in blue bin'; tips = ['Rinse clean', 'Crush to save space', 'Remove paper labels']; binColor = 'blue'; break;
        case 'newspaper': material = 'paper'; category = 'Recyclable'; disposal = 'Recycle in blue bin'; tips = ['Keep dry', 'Bundle together', 'Remove plastic wrap']; binColor = 'blue'; break;
        case 'cardboard': material = 'paper'; category = 'Recyclable'; disposal = 'Recycle in blue bin'; tips = ['Flatten boxes', 'Remove tape', 'Keep dry']; binColor = 'blue'; break;
        case 'battery': material = 'hazardous'; category = 'Special Waste'; disposal = 'Special disposal required'; tips = ['Do not put in bins', 'Take to collection point', 'Tape terminals']; binColor = 'red'; break;
        default: break;
      }

      const mockResult = { name: item.name, material, category, disposal, tips, confidence: '95%', binColor };
      setIsAnalyzing(false);
      if (onResult) onResult(mockResult);
    }, 1000);
  };

  const triggerFileInput = () => {
    fileInputRef.current?.click();
  };

  return (
    <div className="identification-page">
      {/* Hidden canvas for capturing image */}
      <canvas ref={canvasRef} style={{ display: 'none' }}></canvas>

      {/* Camera Modal */}
      <AnimatePresence>
        {isCameraOpen && (
          <motion.div
            className="camera-modal-overlay"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
          >
            <motion.div
              className="camera-modal-content"
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.8, opacity: 0 }}
            >
              <h3>Live Camera Feed</h3>
              <div className="camera-view">
                <video ref={videoRef} autoPlay playsInline muted></video>
              </div>
              <div className="camera-controls">
                <button className="btn btn-primary" onClick={handleCapture}>
                  ðŸ“· Capture Image
                </button>
                <button className="btn btn-secondary" onClick={handleCloseCamera}>
                  Close
                </button>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
      
      <div className="container">
        <motion.button 
          className="back-btn"
          onClick={() => onNavigate('home')}
          initial={{ opacity: 0, x: -20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.5 }}
        >
          â† Back to Home
        </motion.button>

        <motion.div
          className="page-header"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <h1>Identify Your Item</h1>
          <p>Choose your preferred method to identify how to dispose of your item</p>
          <div className="locale-badge">
            ðŸ“ Currently viewing rules for: <strong>{selectedLocale}</strong>
          </div>
        </motion.div>

        {/* Analysis Overlay */}
        <AnimatePresence>
          {isAnalyzing && (
            <motion.div 
              className="analysis-overlay"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
            >
              <motion.div 
                className="analysis-content"
                initial={{ scale: 0.8, opacity: 0 }}
                animate={{ scale: 1, opacity: 1 }}
                transition={{ type: "spring", stiffness: 300, damping: 20 }}
              >
                <motion.div 
                  className="loading-spinner"
                  animate={{ rotate: 360 }}
                  transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
                >
                  â™»ï¸
                </motion.div>
                <h3>Analyzing your item...</h3>
                <p>Our AI is identifying the best disposal method</p>
                <motion.div 
                  className="loading-dots"
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ delay: 0.5 }}
                >
                  <span></span><span></span><span></span>
                </motion.div>
              </motion.div>
            </motion.div>
          )}
        </AnimatePresence>

        {/* Method Tabs */}
        <motion.div 
          className="method-tabs"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.2 }}
        >
          <div className="tab-buttons">
            <button className={`tab-btn ${activeTab === 'camera' ? 'active' : ''}`} onClick={() => setActiveTab('camera')}>ðŸ“· Upload or Capture</button>
            <button className={`tab-btn ${activeTab === 'search' ? 'active' : ''}`} onClick={() => setActiveTab('search')}>ðŸ” Search</button>
            <button className={`tab-btn ${activeTab === 'quick' ? 'active' : ''}`} onClick={() => setActiveTab('quick')}>âš¡ Quick Pick</button>
          </div>

          <div className="tab-content">
            <AnimatePresence mode="wait">
              {activeTab === 'camera' && (
                <motion.div
                  key="camera"
                  initial={{ opacity: 0, x: 20 }}
                  animate={{ opacity: 1, x: 0 }}
                  exit={{ opacity: 0, x: -20 }}
                  transition={{ duration: 0.3 }}
                  className="upload-section"
                >
                  <div className="upload-area">
                    <input type="file" ref={fileInputRef} onChange={handleImageUpload} accept="image/*" className="file-input" />
                    
                    {!imagePreview ? (
                      <div className="upload-placeholder">
                        <div className="upload-icon">ðŸ–¼ï¸</div>
                        <h3>Identify by Image</h3>
                        <p>Upload a photo or use your camera</p>
                        {cameraError && <p className="camera-error">{cameraError}</p>}
                        <div className="upload-options">
                          <button className="btn btn-primary" onClick={triggerFileInput}>
                            Choose from Computer
                          </button>
                          <button className="btn btn-secondary" onClick={handleOpenCamera}>
                            ðŸ“· Use Camera
                          </button>
                        </div>
                      </div>
                    ) : (
                      <div className="image-preview-container">
                        <div className="image-preview">
                          <img src={imagePreview} alt="Uploaded item" />
                          <button className="remove-image-btn" onClick={() => { setSelectedImage(null); setImagePreview(null); }}>âœ•</button>
                        </div>
                        <div className="preview-actions">
                          <button className="btn btn-secondary" onClick={triggerFileInput}>Change Image</button>
                          <button className="btn btn-primary" onClick={handleImageAnalysis}>Analyze Image</button>
                        </div>
                      </div>
                    )}
                  </div>
                </motion.div>
              )}

              {activeTab === 'search' && (
                <motion.div key="search" initial={{ opacity: 0, x: 20 }} animate={{ opacity: 1, x: 0 }} exit={{ opacity: 0, x: -20 }} transition={{ duration: 0.3 }}>
                  <div className="search-section">
                    <form onSubmit={handleTextSearch} className="search-form">
                      <div className="search-input-wrapper">
                        <input type="text" value={searchQuery} onChange={(e) => setSearchQuery(e.target.value)} placeholder="e.g., 'plastic bottle', 'glass jar'" className="search-input"/>
                        <button type="submit" className="search-btn">ðŸ”</button>
                      </div>
                    </form>
                    <div className="search-examples">
                      <h4>Try searching for:</h4>
                      <div className="example-tags">
                        {['plastic bottle', 'glass jar', 'aluminum can', 'newspaper', 'cardboard box', 'food waste', 'battery', 'electronics'].map(tag => (
                          <button key={tag} className="example-tag" onClick={() => setSearchQuery(tag)}>{tag}</button>
                        ))}
                      </div>
                    </div>
                  </div>
                </motion.div>
              )}

              {activeTab === 'quick' && (
                <motion.div key="quick" initial={{ opacity: 0, x: 20 }} animate={{ opacity: 1, x: 0 }} exit={{ opacity: 0, x: -20 }} transition={{ duration: 0.3 }}>
                  <div className="quick-items-section">
                    <h3 className="quick-items-title">Common Items</h3>
                    <p className="quick-items-subtitle">Select an item for instant guidance</p>
                    <div className="quick-items-grid">
                      {quickItems.map((item, index) => (
                        <motion.button key={item.id} className="quick-item-card" onClick={() => handleQuickItemSelect(item.id)} initial={{ opacity: 0, scale: 0.8 }} animate={{ opacity: 1, scale: 1 }} transition={{ duration: 0.4, delay: index * 0.1 }} whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.95 }} style={{ '--item-color': item.color }}>
                          <div className="quick-item-icon">{item.icon}</div>
                          <span className="quick-item-name">{item.name}</span>
                        </motion.button>
                      ))}
                    </div>
                  </div>
                </motion.div>
              )}
            </AnimatePresence>
          </div>
        </motion.div>

        {/* Tips Section */}
        <motion.div className="tips-section" initial={{ opacity: 0, y: 30 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.6, delay: 0.4 }}>
          <h3>ðŸ’¡ Tips for Better Identification</h3>
          <div className="tips-grid">
            <div className="tip-card"><div className="tip-icon">ðŸ“¸</div><h4>Clear Photos</h4><p>Take photos in good lighting with a clear view of the item</p></div>
            <div className="tip-card"><div className="tip-icon">ðŸ§¹</div><h4>Clean Items</h4><p>Make sure items are clean and dry for accurate recycling</p></div>
