import React, { useRef, useState } from 'react';
import { motion } from 'framer-motion';
import './CameraInput.css';

const CameraInput = ({ onImageCapture, onImageUpload }) => {
  const fileInputRef = useRef(null);
  const videoRef = useRef(null);
  const [stream, setStream] = useState(null);
  const [isCameraActive, setIsCameraActive] = useState(false);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        onImageUpload(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const startCamera = async () => {
    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({ 
        video: { facingMode: 'environment' } 
      });
      setStream(mediaStream);
      if (videoRef.current) {
        videoRef.current.srcObject = mediaStream;
      }
      setIsCameraActive(true);
    } catch (error) {
      console.error('Error accessing camera:', error);
      alert('Unable to access camera. Please check permissions.');
    }
  };

  const stopCamera = () => {
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
      setStream(null);
      setIsCameraActive(false);
    }
  };

  const captureImage = () => {
    if (videoRef.current) {
      const canvas = document.createElement('canvas');
      canvas.width = videoRef.current.videoWidth;
      canvas.height = videoRef.current.videoHeight;
      const context = canvas.getContext('2d');
      context.drawImage(videoRef.current, 0, 0);
      const imageData = canvas.toDataURL('image/jpeg');
      onImageCapture(imageData);
      stopCamera();
    }
  };

  return (
    <div className="camera-input">
      {!isCameraActive ? (
        <div className="input-options">
          <motion.div 
            className="input-option"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            <div className="option-icon">üì∑</div>
            <h3>Use Camera</h3>
            <p>Take a photo of your item</p>
            <button className="btn btn-primary" onClick={startCamera}>
              Open Camera
            </button>
          </motion.div>

          <motion.div 
            className="input-option"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            <div className="option-icon">üñºÔ∏è</div>
            <h3>Upload Photo</h3>
            <p>Choose from your gallery</p>
            <button 
              className="btn btn-secondary"
              onClick={() => fileInputRef.current?.click()}
            >
              Choose File
            </button>
            <input
              type="file"
              ref={fileInputRef}
              accept="image/*"
              onChange={handleFileUpload}
              style={{ display: 'none' }}
            />
          </motion.div>
        </div>
      ) : (
        <div className="camera-preview">
          <video ref={videoRef} autoPlay playsInline className="camera-video" />
          <div className="camera-controls">
            <motion.button 
              className="btn btn-accent capture-btn"
              onClick={captureImage}
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
            >
              üì∏
            </motion.button>
            <button className="btn btn-secondary" onClick={stopCamera}>
              Cancel
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CameraInput;
