import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import Header from './components/Header';
import Home from './components/Home';
import Identification from './components/Identification';
import LocalRules from './components/LocalRules';
import Upcycling from './components/Upcycling';
import SchedulePickup from './components/SchedulePickup';
import './App.css';

function App() {
  const [currentPage, setCurrentPage] = useState('home');
  const [darkMode, setDarkMode] = useState(false);
  const [selectedLocale, setSelectedLocale] = useState('India');

  // Initialize dark mode from localStorage or system preference
  useEffect(() => {
    const savedDarkMode = localStorage.getItem('darkMode');
    const savedLocale = localStorage.getItem('selectedLocale');
    
    if (savedDarkMode) {
      setDarkMode(JSON.parse(savedDarkMode));
    } else {
      // Check system preference
      const systemPrefersDark = window.matchMedia('(prefers-color-scheme: dark)').matches;
      setDarkMode(systemPrefersDark);
    }
    
    if (savedLocale) {
      setSelectedLocale(savedLocale);
    }
  }, []);

  // Save dark mode preference to localStorage
  useEffect(() => {
    localStorage.setItem('darkMode', JSON.stringify(darkMode));
    document.body.className = darkMode ? 'dark-mode' : '';
  }, [darkMode]);

  // Save locale preference to localStorage
  useEffect(() => {
    localStorage.setItem('selectedLocale', selectedLocale);
  }, [selectedLocale]);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  const handleNavigate = (page) => {
    setCurrentPage(page);
    // Scroll to top when navigating
    window.scrollTo(0, 0);
  };

  const pageVariants = {
    initial: { opacity: 0, y: 20 },
    in: { opacity: 1, y: 0 },
    out: { opacity: 0, y: -20 }
  };

  const pageTransition = {
    type: "tween",
    ease: "anticipate",
    duration: 0.4
  };

  const renderCurrentPage = () => {
    switch (currentPage) {
      case 'home':
        return (
          <Home 
            onNavigate={handleNavigate}
            selectedLocale={selectedLocale}
            setSelectedLocale={setSelectedLocale}
          />
        );
      case 'identification':
        return (
          <Identification 
            onNavigate={handleNavigate}
            selectedLocale={selectedLocale}
          />
        );
      case 'local-rules':
        return (
          <LocalRules 
            onNavigate={handleNavigate}
            selectedLocale={selectedLocale}
          />
        );
      case 'upcycling':
        return (
          <Upcycling 
            onNavigate={handleNavigate}
            selectedLocale={selectedLocale}
          />
        );
      case 'schedule-pickup':
        return (
          <SchedulePickup 
            onNavigate={handleNavigate}
            selectedLocale={selectedLocale}
          />
        );
      default:
        return (
          <Home 
            onNavigate={handleNavigate}
            selectedLocale={selectedLocale}
            setSelectedLocale={setSelectedLocale}
          />
        );
    }
  };

  return (
    <div className={`app ${darkMode ? 'dark-mode' : ''}`}>
      <Header 
        currentPage={currentPage}
        onNavigate={handleNavigate}
        darkMode={darkMode}
        toggleDarkMode={toggleDarkMode}
      />
      
      <main className="main-content">
        <AnimatePresence mode="wait">
          <motion.div
            key={currentPage}
            initial="initial"
            animate="in"
            exit="out"
            variants={pageVariants}
            transition={pageTransition}
          >
            {renderCurrentPage()}
          </motion.div>
        </AnimatePresence>
      </main>

      {/* Simple Footer */}
      <footer className="app-footer">
        <div className="container">
          <p>&copy; 2024 Smart Recycle Identifier. Making recycling smarter, one item at a time. üåç</p>
        </div>
      </footer>
    </div>
  );
}

export default App;
