import { GoogleGenerativeAI } from "@google/generative-ai";

// Make sure your .env file has the API key
if (!process.env.REACT_APP_GEMINI_API_KEY) {
  console.error('Missing Gemini API key! Please add REACT_APP_GEMINI_API_KEY to your .env file');
}

const genAI = new GoogleGenerativeAI(process.env.REACT_APP_GEMINI_API_KEY || '');

export const analyzeImage = async (imageData) => {
  try {
    const model = genAI.getGenerativeModel({ model: "gemini-pro-vision" });
    
    // Convert base64 to Uint8Array
    const base64Data = imageData.split(',')[1];
    const binaryData = atob(base64Data);
    const bytes = new Uint8Array(binaryData.length);
    for (let i = 0; i < binaryData.length; i++) {
      bytes[i] = binaryData.charCodeAt(i);
    }

    const imageParts = [
      {
        inlineData: {
          data: base64Data,
          mimeType: "image/jpeg"
        },
      },
    ];

    const prompt = "Please analyze this image and identify what type of waste item it is. Provide details about its material type, recyclability, and proper disposal method.";

    const result = await model.generateContent([prompt, ...imageParts]);
    const response = await result.response;
    const text = response.text();

    // Parse the response to match our data structure
    // This is a simple example - you may need to adjust based on actual responses
    return {
      name: text.split('\n')[0],
      material: text.includes('plastic') ? 'Plastic' : 
                text.includes('glass') ? 'Glass' : 
                text.includes('paper') ? 'Paper' : 'Other',
      category: text.includes('recyclable') ? 'Recyclables' : 'General Waste',
      binColor: text.includes('recyclable') ? 'Blue' : 'Black',
      tip: text.split('\n').slice(1).join(' '),
    };
  } catch (error) {
    console.error('Error analyzing image:', error);
    throw error;
  }
};
