import React from 'react';
import { motion } from 'framer-motion';
import './Header.css';

const Header = ({ currentPage, onNavigate, darkMode, toggleDarkMode }) => {
  const navItems = [
    { id: 'home', label: 'Home', icon: 'üè†' },
    { id: 'identification', label: 'Identify', icon: 'üîç' },
    { id: 'local-rules', label: 'Local Rules', icon: 'üìã' },
    { id: 'upcycling', label: 'Upcycling', icon: '‚ôªÔ∏è' },
  ];

  return (
    <header className="header">
      <div className="container">
        <div className="header-content">
          <motion.div
            className="logo"
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5 }}
          >
            <span className="logo-icon">üå±</span>
            <h1>Smart Ecodispose</h1>
          </motion.div>
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5, delay: 0.2 }}
            style={{ display: 'flex', gap: '0.5rem', alignItems: 'center' }}
          >
            <nav className="nav">
              {navItems.map((item, index) => (
                <motion.button
                  key={item.id}
                  className={`nav-btn${currentPage === item.id ? ' active' : ''}`}
                  onClick={() => onNavigate(item.id)}
                  initial={{ opacity: 0, y: -20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.3, delay: index * 0.1 }}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  <span className="nav-icon">{item.icon}</span>
                  {item.label}
                </motion.button>
              ))}
            </nav>
            <motion.button
              className="theme-toggle"
              onClick={toggleDarkMode}
              whileHover={{ scale: 1.1, rotate: 15 }}
              whileTap={{ scale: 0.9 }}
              transition={{ duration: 0.2 }}
            >
              {darkMode ? 'üåô' : '‚òÄÔ∏è'}
            </motion.button>
          </motion.div>
        </div>
      </div>
    </header>
  );
};

export default Header;
