import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { fadeInUp, staggerContainer } from '../../utils/animations';
import './Upcycling.css';

const Upcycling = ({ onNavigate, selectedLocale }) => {
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [selectedProject, setSelectedProject] = useState(null);

  const handleBackToHome = () => {
    if (typeof onNavigate === 'function') {
      onNavigate('home');
    } else {
      console.error("onNavigate prop is not a function or is missing in Upcycling component!");
      // Fallback: try to navigate using window.location if available
      if (window.history.length > 1) {
        window.history.back();
      }
    }
  };

  const categories = [
    { id: 'all', name: 'All Projects', icon: 'üåü' },
    { id: 'easy', name: 'Easy', icon: 'üü¢' },
    { id: 'medium', name: 'Medium', icon: 'üü°' },
    { id: 'hard', name: 'Hard', icon: 'üî¥' }
  ];

  // Mock upcycling projects data
  const upcyclingProjects = {
    'plant-pots': {
      id: 'plant-pots',
      name: 'Plastic Bottle Plant Pots',
      difficulty: 'Easy',
      time: '15-30 mins',
      materials: ['Plastic bottles', 'Scissors', 'Soil', 'Seeds'],
      steps: [
        'Cut plastic bottle in half',
        'Make drainage holes in the bottom',
        'Fill with soil and plant seeds',
        'Water regularly and watch them grow'
      ],
      benefits: 'Reduces plastic waste and creates beautiful plants'
    },
    'bird-feeder': {
      id: 'bird-feeder',
      name: 'Milk Carton Bird Feeder',
      difficulty: 'Easy',
      time: '20 mins',
      materials: ['Milk carton', 'String', 'Bird seed', 'Scissors'],
      steps: [
        'Clean and dry the milk carton',
        'Cut openings for birds to enter',
        'Attach string for hanging',
        'Fill with bird seed and hang outside'
      ],
      benefits: 'Helps local wildlife and reuses packaging'
    },
    'glass-jars': {
      id: 'glass-jars',
      name: 'Glass Jar Storage',
      difficulty: 'Easy',
      time: '5 mins',
      materials: ['Glass jars', 'Labels', 'Decorations'],
      steps: [
        'Clean and dry glass jars thoroughly',
        'Remove any labels or adhesive',
        'Add your own labels for organization',
        'Use for storing spices, crafts, or office supplies'
      ],
      benefits: 'Organizes your space while reducing waste'
    }
  };

  const filteredProjects = selectedCategory === 'all' 
    ? Object.values(upcyclingProjects)
    : Object.values(upcyclingProjects).filter(
        project => project.difficulty.toLowerCase() === selectedCategory
      );

  const featuredProject = upcyclingProjects['plant-pots'];

  return (
    <div className="upcycling-page">
      <motion.div 
        className="upcycling-container"
        variants={staggerContainer}
        initial="initial"
        animate="animate"
      >
        {/* Header with Back Button */}
        <motion.div className="upcycling-header" variants={fadeInUp}>
          <motion.button 
            className="back-btn"
            onClick={handleBackToHome}
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5 }}
            whileHover={{ scale: 1.05, x: -5 }}
            whileTap={{ scale: 0.95 }}
          >
            ‚Üê Back to Home
          </motion.button>
          <h1>‚ôªÔ∏è Upcycling Ideas</h1>
          <p className="subtitle">
            Transform your waste into something useful and beautiful with these creative projects
          </p>
        </motion.div>

        {/* Category Filter */}
        <motion.div className="category-filter" variants={fadeInUp}>
          {categories.map(category => (
            <motion.button
              key={category.id}
              className={`category-btn ${selectedCategory === category.id ? 'active' : ''}`}
              onClick={() => setSelectedCategory(category.id)}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <span className="category-icon">{category.icon}</span>
              {category.name}
            </motion.button>
          ))}
        </motion.div>

        {/* Featured Project */}
        <motion.div className="featured-project" variants={fadeInUp}>
          <div className="featured-badge">‚ú® Featured Project</div>
          <h2>{featuredProject.name}</h2>
          <div className="project-meta">
            <span className="difficulty">
              {featuredProject.difficulty === 'Easy' ? 'üü¢' : 
               featuredProject.difficulty === 'Medium' ? 'üü°' : 'üî¥'} 
              {featuredProject.difficulty}
            </span>
            <span className="time">‚è±Ô∏è {featuredProject.time}</span>
          </div>
          
          <div className="featured-content">
            <div className="materials-section">
              <h3>What You'll Need:</h3>
              <ul>
                {featuredProject.materials.map((material, index) => (
                  <li key={index}>{material}</li>
                ))}
              </ul>
            </div>
            
            <div className="steps-section">
              <h3>Steps:</h3>
              <ol>
                {featuredProject.steps.map((step, index) => (
                  <li key={index}>{step}</li>
                ))}
              </ol>
            </div>
          </div>
          
          <div className="benefits-box">
            <strong>Environmental Benefits:</strong> {featuredProject.benefits}
          </div>
        </motion.div>

        {/* Project Grid */}
        <motion.div className="projects-grid" variants={fadeInUp}>
          <h2>All Upcycling Projects</h2>
          <div className="grid">
            {filteredProjects.map((project, index) => (
              <motion.div
                key={project.id}
                className="project-card"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: index * 0.1 }}
                whileHover={{ y: -10, boxShadow: '0 10px 30px rgba(0,0,0,0.2)' }}
                onClick={() => setSelectedProject(project)}
              >
                <h3>{project.name}</h3>
                <div className="project-meta">
                  <span className="difficulty">
                    {project.difficulty === 'Easy' ? 'üü¢' : 
                     project.difficulty === 'Medium' ? 'üü°' : 'üî¥'} 
                    {project.difficulty}
                  </span>
                  <span className="time">‚è±Ô∏è {project.time}</span>
                </div>
                <p className="project-preview">
                  {project.steps[0].substring(0, 100)}...
                </p>
                <button className="learn-more-btn">Learn More ‚Üí</button>
              </motion.div>
            ))}
          </div>
        </motion.div>

        {/* Project Modal */}
        <AnimatePresence>
          {selectedProject && (
            <motion.div
              className="modal-overlay"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              onClick={() => setSelectedProject(null)}
            >
              <motion.div
                className="modal-content"
                initial={{ scale: 0.8, opacity: 0 }}
                animate={{ scale: 1, opacity: 1 }}
                exit={{ scale: 0.8, opacity: 0 }}
                onClick={(e) => e.stopPropagation()}
              >
                <button 
                  className="modal-close"
                  onClick={() => setSelectedProject(null)}
                >
                  ‚úï
                </button>
                
                <h2>{selectedProject.name}</h2>
                <div className="project-meta">
                  <span className="difficulty">
                    {selectedProject.difficulty === 'Easy' ? 'üü¢' : 
                     selectedProject.difficulty === 'Medium' ? 'üü°' : 'üî¥'} 
                    {selectedProject.difficulty}
                  </span>
                  <span className="time">‚è±Ô∏è {selectedProject.time}</span>
                </div>

                <div className="modal-body">
                  <div className="materials-section">
                    <h3>Materials Needed:</h3>
                    <ul>
                      {selectedProject.materials.map((material, index) => (
                        <li key={index}>{material}</li>
                      ))}
                    </ul>
                  </div>

                  <div className="steps-section">
                    <h3>Step-by-Step Instructions:</h3>
                    <ol>
                      {selectedProject.steps.map((step, index) => (
                        <li key={index}>{step}</li>
                      ))}
                    </ol>
                  </div>

                  <div className="benefits-box">
                    <strong>Why This Matters:</strong> {selectedProject.benefits}
                  </div>
                </div>
              </motion.div>
            </motion.div>
          )}
        </AnimatePresence>
      </motion.div>
    </div>
  );
};

export default Upcycling;
