import React, { useRef, useState, useEffect, useCallback } from 'react';
import './CameraInput.css';

const CameraInput = ({ onImageCapture, onImageUpload }) => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const fileInputRef = useRef(null);

  const [stream, setStream] = useState(null);
  const [isCameraActive, setIsCameraActive] = useState(false);
  const [cameraError, setCameraError] = useState(null);
  const [videoReady, setVideoReady] = useState(false);
  const [isCapturing, setIsCapturing] = useState(false);
  const [previewImage, setPreviewImage] = useState(null);
  const [isDragging, setIsDragging] = useState(false);

  // Request camera stream
  const startCamera = useCallback(async () => {
    setCameraError(null);
    setIsCameraActive(false);
    setVideoReady(false);

    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({ video: true });
      setStream(mediaStream);
      setIsCameraActive(true);
      if (videoRef.current) {
        videoRef.current.srcObject = mediaStream;
        videoRef.current.play().catch(console.warn);
      }
    } catch (error) {
      setCameraError('Error accessing the camera: ' + error.message);
      setIsCameraActive(false);
    }
  }, []);

  // Stop camera stream
  const stopCamera = useCallback(() => {
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
      setStream(null);
      setIsCameraActive(false);
      setVideoReady(false);
    }
  }, [stream]);

  // Video event handlers
  const onLoadedMetadata = () => setVideoReady(true);
  const onCanPlay = () => setVideoReady(true);

  // Capture current video frame to canvas as image
  const captureImage = () => {
    if (!videoRef.current || !canvasRef.current) return;
    setIsCapturing(true);

    const video = videoRef.current;
    const canvas = canvasRef.current;
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    const ctx = canvas.getContext('2d');
    ctx.drawImage(video, 0, 0, canvas.width, canvas.height);

    canvas.toBlob(blob => {
      if (blob) {
        const imgUrl = URL.createObjectURL(blob);
        setPreviewImage(imgUrl);
        if (onImageCapture) onImageCapture(blob);
      }
      setIsCapturing(false);
    }, 'image/jpeg');
  };

  // Handle file upload (drag & drop or input file)
  const handleFileChange = e => {
    const file = e.target.files && e.target.files[0];
    if (file && file.type.startsWith('image/')) {
      const url = URL.createObjectURL(file);
      setPreviewImage(url);
      if (onImageUpload) onImageUpload(file);
    }
  };

  // Drag & drop handlers
  const onDragOver = e => {
    e.preventDefault();
    setIsDragging(true);
  };
  const onDragLeave = e => {
    e.preventDefault();
    setIsDragging(false);
  };
  const onDrop = e => {
    e.preventDefault();
    setIsDragging(false);
    const file = e.dataTransfer.files && e.dataTransfer.files[0];
    if (file && file.type.startsWith('image/')) {
      const url = URL.createObjectURL(file);
      setPreviewImage(url);
      if (onImageUpload) onImageUpload(file);
    }
  };

  useEffect(() => {
    startCamera();
    return () => stopCamera();
  }, [startCamera, stopCamera]);

  return (
    <div className="camera-input-wrapper">
      <div
        className={`drag-drop-area${isDragging ? ' dragging' : ''}`}
        onDragOver={onDragOver}
        onDragLeave={onDragLeave}
        onDrop={onDrop}
        onClick={() => fileInputRef.current?.click()}
      >
        {!previewImage && (
          <>
            {cameraError && <p className="camera-error">{cameraError}</p>}
            {!isCameraActive && !cameraError && <p>Loading camera...</p>}
            <video
              ref={videoRef}
              onLoadedMetadata={onLoadedMetadata}
              onCanPlay={onCanPlay}
              muted
              playsInline
              autoPlay
              className={`camera-video${videoReady ? ' ready' : ''}`}
            />
            {!videoReady && <div className="loading-spinner">Loading...</div>}
          </>
        )}
        {previewImage && (
          <img className="preview-image" src={previewImage} alt="Preview" />
        )}
        <canvas ref={canvasRef} style={{ display: 'none' }} />
      </div>
      <input
        type="file"
        accept="image/*"
        ref={fileInputRef}
        onChange={handleFileChange}
        style={{ display: 'none' }}
      />
      <div className="camera-controls">
        <button
          onClick={captureImage}
          disabled={!isCameraActive || isCapturing}
          className="capture-button"
        >
          {isCapturing ? 'Capturing...' : 'Capture'}
        </button>
        <button onClick={startCamera} disabled={isCameraActive} className="start-button">
          Start Camera
        </button>
        <button onClick={stopCamera} disabled={!isCameraActive} className="stop-button">
          Stop Camera
        </button>
        <button onClick={() => setPreviewImage(null)} disabled={!previewImage} className="clear-button">
          Clear Preview
        </button>
      </div>
    </div>
  );
};

export default CameraInput;
