import { GoogleGenerativeAI } from "@google/generative-ai";

if (!process.env.REACT_APP_GEMINI_API_KEY) {
  console.error('Missing Gemini API key! Please add REACT_APP_GEMINI_API_KEY to your .env file');
}

const genAI = new GoogleGenerativeAI(process.env.REACT_APP_GEMINI_API_KEY || '');

export const analyzeImage = async (imageData) => {
  try {
    const model = genAI.getGenerativeModel({ model: "gemini-2.5-flash" });
    
    // Ensure we have valid base64 data
    let base64Data;
    try {
      if (imageData instanceof Blob || imageData instanceof File) {
        base64Data = await new Promise((resolve, reject) => {
          const reader = new FileReader();
          reader.onloadend = () => {
            const dataUrl = reader.result;
            resolve(dataUrl.split(',')[1]);
          };
          reader.onerror = reject;
          reader.readAsDataURL(imageData);
        });
      } else if (typeof imageData === 'string' && imageData.includes('base64')) {
        base64Data = imageData.split(',')[1];
      } else {
        throw new Error('Invalid image data format');
      }

      const imageParts = [
        {
          inlineData: {
            data: base64Data,
            mimeType: "image/jpeg"
          },
        },
      ];

      const prompt = `Analyze this image and identify what type of waste item it is. Format the response as:
      Name: [item name]
      Material: [material type]
      Category: [waste category]
      BinColor: [bin color]
      Tip: [disposal instructions]`;

      const result = await model.generateContent([prompt, imageParts[0]]);
      const response = await result.response;
      const text = response.text();

      // Parse the response
      const lines = text.split('\n');
      const data = {};
      
      lines.forEach(line => {
        if (line.includes(':')) {
          const [key, value] = line.split(':').map(s => s.trim());
          data[key.toLowerCase()] = value;
        }
      });

      return {
        id: Date.now().toString(),
        name: data.name || 'Unknown Item',
        material: data.material || 'Unknown',
        category: data.category || 'General Waste',
        binColor: data.bincolor || 'Black',
        tip: data.tip || 'Please dispose of properly',
      };
    } catch (error) {
      throw new Error(`Invalid image format: ${error.message}`);
    }
  } catch (error) {
    console.error('Error in analyzeImage:', error);
    throw new Error(`Failed to analyze image: ${error.message}`);
  }
};
