import React, { useState, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { fadeInUp, staggerContainer } from '../../utils/animations';
import './Identification.css';

const Identification = ({ onNavigate, selectedLocale, onResult }) => {
  const [activeTab, setActiveTab] = useState('camera');
  const [searchQuery, setSearchQuery] = useState('');
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const fileInputRef = useRef(null);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedImage(file);
      const reader = new FileReader();
      reader.onload = (e) => {
        setImagePreview(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleImageAnalysis = () => {
    if (!selectedImage) return;
    
    setIsAnalyzing(true);
    // Simulate AI analysis
    setTimeout(() => {
      setIsAnalyzing(false);
      const mockResult = {
        name: 'Uploaded Item',
        material: 'plastic',
        disposal: 'Recycle in blue bin',
        tips: ['Rinse before recycling', 'Remove caps if separate', 'Check local guidelines'],
        confidence: '85%'
      };
      if (onResult) onResult(mockResult);
    }, 2000);
  };

  const handleTextSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      setIsAnalyzing(true);
      // Simulate search and analysis
      setTimeout(() => {
        setIsAnalyzing(false);
        // Enhanced mock result based on search query
        const query = searchQuery.toLowerCase();
        let material = 'mixed';
        let disposal = 'Check local guidelines';
        let tips = ['Clean before disposal', 'Separate different materials'];

        if (query.includes('plastic') || query.includes('bottle')) {
          material = 'plastic';
          disposal = 'Recycle in blue bin';
          tips = ['Rinse thoroughly', 'Remove caps', 'Flatten if possible'];
        } else if (query.includes('glass') || query.includes('jar')) {
          material = 'glass';
          disposal = 'Recycle in green bin';
          tips = ['Rinse clean', 'Remove lids', 'No broken glass in recycling'];
        } else if (query.includes('metal') || query.includes('can')) {
          material = 'metal';
          disposal = 'Recycle in blue bin';
          tips = ['Rinse clean', 'Remove labels if easy', 'Crush to save space'];
        } else if (query.includes('paper') || query.includes('cardboard')) {
          material = 'paper';
          disposal = 'Recycle in blue bin';
          tips = ['Keep dry and clean', 'Flatten boxes', 'Remove plastic wrapping'];
        } else if (query.includes('food') || query.includes('organic')) {
          material = 'organic';
          disposal = 'Compost in green bin';
          tips = ['No plastic contamination', 'Use compostable bags', 'Keep lid closed'];
        } else if (query.includes('battery')) {
          material = 'hazardous';
          disposal = 'Special disposal required';
          tips = ['Do not put in regular bins', 'Take to collection point', 'Tape terminals if lithium'];
        }

        const mockResult = {
          name: searchQuery,
          material: material,
          disposal: disposal,
          tips: tips,
          confidence: '90%'
        };
        if (onResult) onResult(mockResult);
      }, 1500);
    }
  };

  const quickItems = [
    { name: 'Plastic Bottle', icon: '🧴', id: 'plastic-bottle', color: '#2196F3' },
    { name: 'Glass Jar', icon: '🍶', id: 'glass-jar', color: '#4CAF50' },
    { name: 'Aluminum Can', icon: '🥫', id: 'aluminum-can', color: '#FF9800' },
    { name: 'Newspaper', icon: '📰', id: 'newspaper', color: '#795548' },
    { name: 'Cardboard', icon: '📦', id: 'cardboard', color: '#8BC34A' },
    { name: 'Battery', icon: '🔋', id: 'battery', color: '#F44336' },
  ];

  const handleQuickItemSelect = (itemId) => {
    setIsAnalyzing(true);
    setTimeout(() => {
      const item = quickItems.find(item => item.id === itemId);
      let disposal = 'Check local guidelines';
      let tips = ['Identify material first', 'Clean if necessary'];

      switch(itemId) {
        case 'plastic-bottle':
          disposal = 'Recycle in blue bin';
          tips = ['Rinse thoroughly', 'Remove cap', 'Flatten if possible'];
          break;
        case 'glass-jar':
          disposal = 'Recycle in green bin';
          tips = ['Rinse clean', 'Remove metal lids', 'No broken glass'];
          break;
        case 'aluminum-can':
          disposal = 'Recycle in blue bin';
          tips = ['Rinse clean', 'Crush to save space', 'Remove paper labels'];
          break;
        case 'newspaper':
          disposal = 'Recycle in blue bin';
          tips = ['Keep dry', 'Bundle together', 'Remove plastic wrap'];
          break;
        case 'cardboard':
          disposal = 'Recycle in blue bin';
          tips = ['Flatten boxes', 'Remove tape', 'Keep dry'];
          break;
        case 'battery':
          disposal = 'Special disposal required';
          tips = ['Do not put in bins', 'Take to collection point', 'Tape terminals'];
          break;
      }

      const mockResult = {
        name: item.name,
        material: item.name.split(' ')[0].toLowerCase(),
        disposal: disposal,
        tips: tips,
        confidence: '95%'
      };
      setIsAnalyzing(false);
      if (onResult) onResult(mockResult);
    }, 1000);
  };

  const triggerFileInput = () => {
    fileInputRef.current?.click();
  };

  return (
    <div className="identification-page">
      <div className="container">
        <motion.button 
          className="back-btn"
          onClick={() => onNavigate('home')}
          initial={{ opacity: 0, x: -20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.5 }}
        >
          ← Back to Home
        </motion.button>

        <motion.div
          className="page-header"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <h1>Identify Your Item</h1>
          <p>Choose your preferred method to identify how to dispose of your item</p>
          <div className="locale-badge">
            📍 Currently viewing rules for: <strong>{selectedLocale}</strong>
          </div>
        </motion.div>

        {/* Analysis Overlay */}
        <AnimatePresence>
          {isAnalyzing && (
            <motion.div 
              className="analysis-overlay"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
            >
              <motion.div 
                className="analysis-content"
                initial={{ scale: 0.8, opacity: 0 }}
                animate={{ scale: 1, opacity: 1 }}
                transition={{ type: "spring", stiffness: 300, damping: 20 }}
              >
                <motion.div 
                  className="loading-spinner"
                  animate={{ rotate: 360 }}
                  transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
                >
                  ♻️
                </motion.div>
                <h3>Analyzing your item...</h3>
                <p>Our AI is identifying the best disposal method</p>
                <motion.div 
                  className="loading-dots"
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ delay: 0.5 }}
                >
                  <span></span>
                  <span></span>
                  <span></span>
                </motion.div>
              </motion.div>
            </motion.div>
          )}
        </AnimatePresence>

        {/* Method Tabs */}
        <motion.div 
          className="method-tabs"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.2 }}
        >
          <div className="tab-buttons">
            <button 
              className={`tab-btn ${activeTab === 'camera' ? 'active' : ''}`}
              onClick={() => setActiveTab('camera')}
            >
              📷 Upload Image
            </button>
            <button 
              className={`tab-btn ${activeTab === 'search' ? 'active' : ''}`}
              onClick={() => setActiveTab('search')}
            >
              🔍 Search
            </button>
            <button 
              className={`tab-btn ${activeTab === 'quick' ? 'active' : ''}`}
              onClick={() => setActiveTab('quick')}
            >
              ⚡ Quick Pick
            </button>
          </div>

          <div className="tab-content">
            <AnimatePresence mode="wait">
              {activeTab === 'camera' && (
                <motion.div
                  key="camera"
                  initial={{ opacity: 0, x: 20 }}
                  animate={{ opacity: 1, x: 0 }}
                  exit={{ opacity: 0, x: -20 }}
                  transition={{ duration: 0.3 }}
                  className="upload-section"
                >
                  <div className="upload-area">
                    <input
                      type="file"
                      ref={fileInputRef}
                      onChange={handleImageUpload}
                      accept="image/*"
                      className="file-input"
                    />
                    
                    {!imagePreview ? (
                      <div className="upload-placeholder" onClick={triggerFileInput}>
                        <div className="upload-icon">📸</div>
                        <h3>Upload Item Image</h3>
                        <p>Click here or drag & drop an image of your item</p>
                        <div className="upload-features">
                          <div className="feature">
                            <span>✅</span>
                            <span>Supports JPG, PNG, WEBP</span>
                          </div>
                          <div className="feature">
                            <span>✅</span>
                            <span>AI-powered analysis</span>
                          </div>
                          <div className="feature">
                            <span>✅</span>
                            <span>Instant results</span>
                          </div>
                        </div>
                        <button className="btn btn-primary upload-btn">
                          Choose from Computer
                        </button>
                      </div>
                    ) : (
                      <div className="image-preview-container">
                        <div className="image-preview">
                          <img src={imagePreview} alt="Uploaded item" />
                          <button 
                            className="remove-image-btn"
                            onClick={() => {
                              setSelectedImage(null);
                              setImagePreview(null);
                            }}
                          >
                            ✕
                          </button>
                        </div>
                        <div className="preview-actions">
                          <button className="btn btn-secondary" onClick={triggerFileInput}>
                            Change Image
                          </button>
                          <button className="btn btn-primary" onClick={handleImageAnalysis}>
                            Analyze Image
                          </button>
                        </div>
                      </div>
                    )}
                  </div>
                </motion.div>
              )}

              {activeTab === 'search' && (
                <motion.div
                  key="search"
                  initial={{ opacity: 0, x: 20 }}
                  animate={{ opacity: 1, x: 0 }}
                  exit={{ opacity: 0, x: -20 }}
                  transition={{ duration: 0.3 }}
                >
                  <div className="search-section">
                    <form onSubmit={handleTextSearch} className="search-form">
                      <div className="search-input-wrapper">
                        <input
                          type="text"
                          value={searchQuery}
                          onChange={(e) => setSearchQuery(e.target.value)}
                          placeholder="Describe your item (e.g., 'plastic bottle', 'glass jar', 'food scraps')"
                          className="search-input"
                        />
                        <button type="submit" className="search-btn">
                          🔍
                        </button>
                      </div>
                    </form>
                    
                    <div className="search-examples">
                      <h4>Try searching for:</h4>
                      <div className="example-tags">
                        {['plastic bottle', 'glass jar', 'aluminum can', 'newspaper', 'cardboard box', 'food waste', 'battery', 'electronics'].map(tag => (
                          <button
                            key={tag}
                            className="example-tag"
                            onClick={() => setSearchQuery(tag)}
                          >
                            {tag}
                          </button>
                        ))}
                      </div>
                    </div>
                  </div>
                </motion.div>
              )}

              {activeTab === 'quick' && (
                <motion.div
                  key="quick"
                  initial={{ opacity: 0, x: 20 }}
                  animate={{ opacity: 1, x: 0 }}
                  exit={{ opacity: 0, x: -20 }}
                  transition={{ duration: 0.3 }}
                >
                  <div className="quick-items-section">
                    <h3 className="quick-items-title">Common Items</h3>
                    <p className="quick-items-subtitle">Select an item to get instant disposal guidance</p>
                    <div className="quick-items-grid">
                      {quickItems.map((item, index) => (
                        <motion.button
                          key={item.id}
                          className="quick-item-card"
                          onClick={() => handleQuickItemSelect(item.id)}
                          initial={{ opacity: 0, scale: 0.8 }}
                          animate={{ opacity: 1, scale: 1 }}
                          transition={{ duration: 0.4, delay: index * 0.1 }}
                          whileHover={{ scale: 1.05 }}
                          whileTap={{ scale: 0.95 }}
                          style={{ '--item-color': item.color }}
                        >
                          <div className="quick-item-icon">{item.icon}</div>
                          <span className="quick-item-name">{item.name}</span>
                        </motion.button>
                      ))}
                    </div>
                  </div>
                </motion.div>
              )}
            </AnimatePresence>
          </div>
        </motion.div>

        {/* Tips Section */}
        <motion.div 
          className="tips-section"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.4 }}
        >
          <h3>💡 Tips for Better Identification</h3>
          <div className="tips-grid">
            <div className="tip-card">
              <div className="tip-icon">📸</div>
              <h4>Clear Photos</h4>
              <p>Take photos in good lighting with a clear view of the item</p>
            </div>
            <div className="tip-card">
              <div className="tip-icon">🧹</div>
              <h4>Clean Items</h4>
              <p>Make sure items are clean and dry for accurate recycling</p>
            </div>
            <div className="tip-card">
              <div className="tip-icon">🏷️</div>
              <h4>Check Labels</h4>
              <p>Look for recycling symbols and material information</p>
            </div>
            <div className="tip-card">
              <div className="tip-icon">🔍</div>
              <h4>Be Specific</h4>
              <p>Use detailed descriptions when searching for items</p>
            </div>
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default Identification;
